// Error Boundary Component
class ErrorBoundary extends Component {
  constructor(props) {
    super(props);
    this.state = { hasError: false };
  }

  static getDerivedStateFromError(error) {
    return { hasError: true };
  }

  componentDidCatch(error, errorInfo) {
    console.log('Erro:', error, errorInfo);
  }

  render() {
    if (this.state.hasError) {
      return <h1>Algo deu errado.</h1>;
    }
    return this.props.children;
  }
}

// ðŸ”¥ COMPONENTE App PRINCIPAL CORRIGIDO
export default function App() {
  // âœ… CORRETO: Todos os hooks DENTRO do componente
  const [showDeclaracaoModal, setShowDeclaracaoModal] = useState(false);
  const [showBoletimModal, setShowBoletimModal] = useState(false);
  const [students, setStudents] = useState([]);
  const [allStudents, setAllStudents] = useState([]);
  const [q, setQ] = useState('');
  const [page, setPage] = useState(1);
  const [limit] = useState(50);
  const [loading, setLoading] = useState(false);
  const [selected, setSelected] = useState(null);
  const [editing, setEditing] = useState(false);
  const [creating, setCreating] = useState(false);
  const [total, setTotal] = useState(0);
  const [totalPages, setTotalPages] = useState(0);
  const [seriesStats, setSeriesStats] = useState({});
  const [localidadeStats, setLocalidadeStats] = useState({});
  const [dashboardLoading, setDashboardLoading] = useState(false);
  const [documentType, setDocumentType] = useState('');
  const [showDataPanel, setShowDataPanel] = useState(true);
  const [disciplinas, setDisciplinas] = useState([]);
  
  // Estados para controlar visibilidade dos dashboards
  const [showSeriesDashboard, setShowSeriesDashboard] = useState(false);
  const [showLocalidadeDashboard, setShowLocalidadeDashboard] = useState(false);

  // ðŸ”¥ FUNÃ‡ÃƒO PARA TOGGLE DO PAINEL DE DADOS
  const toggleDataPanel = () => {
    setShowDataPanel(!showDataPanel);
  };

  // ðŸ”¥ FUNÃ‡ÃƒO PARA ABRIR MODAL DE BOLETIM
  const openBoletimModal = () => {
    if (!selected) {
      alert('Selecione um aluno primeiro');
      return;
    }
    setShowBoletimModal(true);
  };

  // ðŸ”¥ CORREÃ‡ÃƒO: Aluno vazio para criaÃ§Ã£o com TODOS os campos
  const emptyStudent = {
    nome: '',
    dataNascimento: '',
    cpf: '',
    cartaoSUS: '',
    serieAno: '',
    turma: '',
    turno: '',
    status: 'Matriculado',
    transporte: '',
    localidade: '',
    nomeMae: '',
    nomePai: ''
  };

  // Carregar TODOS os alunos para o dashboard
  const loadAllStudents = async () => {
    setDashboardLoading(true);
    try {
      const resp = await fetchStudents({ page: 1, limit: 1000, q: '' });
      setAllStudents(resp.data || []);
    } catch (error) {
      console.error('Erro ao carregar dados do dashboard:', error);
    } finally {
      setDashboardLoading(false);
    }
  };

  // Carregar alunos com busca inteligente
  async function load(searchTerm = q) {
    setLoading(true);
    try {
      const resp = await fetchStudents({ page, limit, q: searchTerm });
      setStudents(resp.data || []);
      setTotal(resp.total || 0);
      setTotalPages(resp.totalPages || 0);
    } catch (error) {
      console.error('Erro ao carregar alunos:', error);
      alert('Erro ao carregar dados');
    } finally {
      setLoading(false);
    }
  }

  // Calcular estatÃ­sticas com TODOS os alunos
  useEffect(() => {
    if (allStudents.length > 0) {
      const statsSeries = {};
      const statsLocalidades = {};
      let totalGeral = 0;
      
      allStudents.forEach(student => {
        const serie = student.serieAno?.toString().trim() || 'NÃ£o informada';
        const turma = student.turma?.toString().trim() || 'Sem turma';
        const localidade = student.localidade?.toString().trim() || 'NÃ£o informada';
        
        totalGeral++;
        
        // EstatÃ­stica por sÃ©rie
        if (!statsSeries[serie]) {
          statsSeries[serie] = {
            count: 0,
            turmas: {}
          };
        }
        statsSeries[serie].count++;
        
        if (!statsSeries[serie].turmas[turma]) {
          statsSeries[serie].turmas[turma] = 0;
        }
        statsSeries[serie].turmas[turma]++;
        
        // EstatÃ­stica por localidade
        if (!statsLocalidades[localidade]) {
          statsLocalidades[localidade] = {
            count: 0,
            series: {}
          };
        }
        statsLocalidades[localidade].count++;
        
        // EstatÃ­stica por sÃ©rie dentro da localidade
        if (!statsLocalidades[localidade].series[serie]) {
          statsLocalidades[localidade].series[serie] = 0;
        }
        statsLocalidades[localidade].series[serie]++;
      });
      
      statsSeries.totalGeral = totalGeral;
      setSeriesStats(statsSeries);
      setLocalidadeStats(statsLocalidades);
    } else {
      setSeriesStats({});
      setLocalidadeStats({});
    }
  }, [allStudents]);

  // Carregar dados do dashboard na inicializaÃ§Ã£o
  useEffect(() => {
    loadAllStudents();
    load();
  }, []);

  // Debounce para pesquisa
  const debouncedSearch = useCallback((searchTerm) => {
    const timer = setTimeout(() => {
      setPage(1);
      load(searchTerm);
    }, 500);
    return () => clearTimeout(timer);
  }, []);

  useEffect(() => {
    if (q !== '') {
      debouncedSearch(q);
    } else {
      setPage(1);
      load();
    }
  }, [q, debouncedSearch]);

  // Recarregar quando mudar pÃ¡gina
  useEffect(() => { 
    if (q === '') {
      load();
    }
  }, [page]);

  // Recarregar dashboard apÃ³s criar/editar/excluir
  const handleStudentChange = () => {
    loadAllStudents();
    load();
  };

  // Busca manual
  function handleSearch() {
    setPage(1);
    load();
  }

  // Abrir detalhes do aluno
  async function openDetail(id) {
    try {
      console.log('ðŸ” Buscando aluno ID:', id);
      const s = await getStudent(id);
      console.log('âœ… Aluno carregado:', s);
      setSelected(s);
      setEditing(false);
      setCreating(false);
      setDocumentType(''); // Resetar tipo de documento
    } catch (error) {
      console.error('âŒ Erro ao carregar detalhes:', error);
      alert('Erro ao carregar detalhes do aluno: ' + error.message);
    }
  }

  // ðŸ”¥ CORREÃ‡ÃƒO: FunÃ§Ã£o para limpar dados antes do envio
  const cleanStudentData = (student) => {
    const cleaned = { ...student };
    
    // Remover campos vazios ou com nomes invÃ¡lidos
    Object.keys(cleaned).forEach(key => {
      // Remover campos com nomes vazios ou apenas espaÃ§os
      if (!key || key.trim() === '') {
        delete cleaned[key];
      }
      // Remover campos com valores undefined ou null
      if (cleaned[key] === undefined || cleaned[key] === null) {
        delete cleaned[key];
      }
      // Converter campos string vazios para undefined
      if (typeof cleaned[key] === 'string' && cleaned[key].trim() === '') {
        cleaned[key] = undefined;
      }
    });
    
    return cleaned;
  };

  // Salvar ediÃ§Ã£o
  async function save() {
    if (!selected || !selected._id) {
      alert('Nenhum aluno selecionado para salvar');
      return;
    }
    try {
      console.log('ðŸ’¾ Salvando aluno:', selected);
      
      // ðŸ”¥ CORREÃ‡ÃƒO: Limpar dados antes do envio
      const cleanedData = cleanStudentData(selected);
      console.log('ðŸ§¹ Dados limpos:', cleanedData);
      
      const res = await updateStudent(selected._id, cleanedData);
      setSelected(res);
      setEditing(false);
      handleStudentChange();
      alert('Aluno atualizado com sucesso!');
    } catch (error) {
      console.error('âŒ Erro ao salvar:', error);
      alert('Erro ao salvar alteraÃ§Ãµes: ' + error.message);
    }
  }

  // Excluir aluno
  async function remove(id) {
    if (!id) {
      alert('ID do aluno nÃ£o encontrado');
      return;
    }
    
    if (!window.confirm('Tem certeza que deseja excluir este aluno?')) return;
    
    try {
      console.log('ðŸ—‘ï¸ Excluindo aluno ID:', id);
      await deleteStudent(id);
      setSelected(null);
      handleStudentChange();
      alert('Aluno excluÃ­do com sucesso!');
    } catch (error) {
      console.error('âŒ Erro ao excluir:', error);
      alert('Erro ao excluir aluno: ' + error.message);
    }
  }

  // Criar novo aluno
  async function createNewStudent() {
    if (!selected) {
      alert('Preencha os dados do aluno');
      return;
    }

    // ValidaÃ§Ã£o bÃ¡sica no frontend
    if (!selected.nome || !selected.nome.trim()) {
      alert('Nome Ã© obrigatÃ³rio');
      return;
    }

    try {
      console.log('âž• Criando novo aluno:', selected);
      
      // ðŸ”¥ CORREÃ‡ÃƒO: Limpar dados antes do envio
      const cleanedData = cleanStudentData(selected);
      console.log('ðŸ§¹ Dados limpos para criaÃ§Ã£o:', cleanedData);

      const createdStudent = await createStudent(cleanedData);
      
      alert('Aluno criado com sucesso!');
      setCreating(false);
      setSelected(null);
      handleStudentChange();
      
    } catch (error) {
      console.error('âŒ Erro ao criar aluno:', error);
      
      // Mensagem de erro mais amigÃ¡vel
      let errorMessage = 'Erro ao criar aluno';
      
      if (error.message.includes('400')) {
        errorMessage = 'Dados invÃ¡lidos. Verifique os campos obrigatÃ³rios.';
      } else if (error.message.includes('CPF jÃ¡ cadastrado')) {
        errorMessage = 'CPF jÃ¡ estÃ¡ cadastrado no sistema.';
      } else if (error.message.includes('NetworkError')) {
        errorMessage = 'Erro de conexÃ£o. Verifique se o servidor estÃ¡ rodando.';
      } else {
        errorMessage = error.message || 'Erro desconhecido ao criar aluno';
      }
      
      alert(errorMessage);
    }
  }

  // Iniciar criaÃ§Ã£o
  function startCreate() {
    setSelected({...emptyStudent});
    setCreating(true);
    setEditing(false);
    setDocumentType('');
  }

  // Cancelar criaÃ§Ã£o
  function cancelCreate() {
    setCreating(false);
    setSelected(null);
    setDocumentType('');
  }

  // Gerar documentos
  function generateDocument(type) {
    if (!selected) {
      alert('Selecione um aluno primeiro');
      return;
    }

    setDocumentType(type);
    
    // Simular geraÃ§Ã£o de documento
    const docContent = {
      declaraÃ§Ã£o: `DeclaraÃ§Ã£o para ${selected.nome}`,
      boletim: `Boletim de ${selected.nome} - ${selected.serieAno}Âª SÃ©rie ${selected.turma}`,
      historico: `HistÃ³rico Escolar de ${selected.nome}`
    };

    const content = docContent[type];
    
    // Criar e baixar documento
    const element = document.createElement('a');
    const file = new Blob([content], { type: 'text/plain' });
    element.href = URL.createObjectURL(file);
    element.download = `${type}_${selected.nome}.txt`;
    document.body.appendChild(element);
    element.click();
    document.body.removeChild(element);
    
    alert(`${type.charAt(0).toUpperCase() + type.slice(1)} gerado com sucesso!`);
  }

  // ðŸ”¥ FUNÃ‡ÃƒO PARA SALVAR NOTAS
  async function salvarNotas() {
    if (!selected) return;
    
    try {
      const alunoAtualizado = {
        ...selected,
        notas: disciplinas
      };
      
      // ðŸ”¥ CORREÃ‡ÃƒO: Limpar dados antes do envio
      const cleanedData = cleanStudentData(alunoAtualizado);
      
      await updateStudent(selected._id, cleanedData);
      setSelected(alunoAtualizado);
      setShowBoletimModal(false);
      alert('Notas salvas com sucesso!');
    } catch (error) {
      console.error('Erro ao salvar notas:', error);
      alert('Erro ao salvar notas: ' + error.message);
    }
  }

  // Pesquisas rÃ¡pidas por sÃ©rie/turma
