import jsPDF from "jspdf";

export function gerarDeclaracao(aluno) {

  const doc = new jsPDF("portrait", "pt", "a4");
  const margem = 40;
  const largura = doc.internal.pageSize.getWidth();
  const altura = doc.internal.pageSize.getHeight();

  // ğŸ”¹ 1. CabeÃ§alho simples (como no modelo do site)
  doc.setFont("Times", "Bold");
  doc.setFontSize(14);
  doc.text("E. E. F. NAIR CUNHA DE AGUIAR", largura / 2, 60, { align: "center" });

  doc.setFont("Times", "Normal");
  doc.setFontSize(12);
  doc.text("SÃ­tio Oiticica â€“ Frecheirinha â€“ CE", largura / 2, 80, { align: "center" });

  // ğŸ”¹ 2. TÃ­tulo
  doc.setFont("Times", "Bold");
  doc.setFontSize(18);
  doc.text("DECLARAÃ‡ÃƒO", largura / 2, 130, { align: "center" });

  // ğŸ”¹ 3. Texto principal
  doc.setFont("Times", "Normal");
  doc.setFontSize(14);

  const nome = aluno.nome;
  const serie = aluno.serieAno;
  const turma = aluno.turma || "";
  const localidade = aluno.localidade || "";
  const dataNasc = aluno.dataNascimento
    ? new Date(aluno.dataNascimento).toLocaleDateString("pt-BR")
    : "";

  // FÃ³rmula Fundamental I / II
  const serieNum = parseInt(serie);
  const nivel =
    serieNum >= 1 && serieNum <= 5 ? "Ensino Fundamental I" : "Ensino Fundamental II";

  // FÃ³rmula aluno / aluna pelo final do nome
  const sexo = nome.endsWith("a") ? "aluna" : "aluno";

  const texto =
    `   Declaramos, para os devidos fins, que ${nome}, ` +
    `${sexo}, nascid${sexo === "aluna" ? "a" : "o"} em ${dataNasc}, ` +
    `estÃ¡ devidamente matriculad${sexo === "aluna" ? "a" : "o"} e frequentando o(a) ` +
    `${serie}Âº ano ${turma}, pertencente ao ${nivel} ` +
    `desta instituiÃ§Ã£o de ensino, situada no SÃ­tio Oiticica, Frecheirinhaâ€“CE.`;

  const linhas = doc.splitTextToSize(texto, largura - margem * 2);

  doc.text(linhas, margem, 200, { align: "justify" });

  // ğŸ”¹ 4. RodapÃ© com data automÃ¡tica
  const hoje = new Date().toLocaleDateString("pt-BR");

  doc.text(`SÃ­tio Oiticica, Frecheirinha â€“ CE, ${hoje}.`, margem, 330);

  // ğŸ”¹ 5. Assinatura
  doc.setFont("Times", "Bold");
  doc.setFontSize(14);
  doc.text("_________________________________", largura / 2, 420, { align: "center" });
  doc.text("Maria do Socorro Oliveira de Aguiar", largura / 2, 440, { align: "center" });
  doc.text("Diretora Escolar", largura / 2, 460, { align: "center" });

  // ğŸ”¹ 6. Baixar PDF
  doc.save(`Declaracao_${nome}.pdf`);
}
