import React, { useEffect, useState, useCallback } from 'react';
import { fetchStudents, getStudent, updateStudent, deleteStudent } from './api';

export default function App() {
  const [students, setStudents] = useState([]);
  const [q, setQ] = useState('');
  const [page, setPage] = useState(1);
  const [limit] = useState(50);
  const [loading, setLoading] = useState(false);
  const [selected, setSelected] = useState(null);
  const [editing, setEditing] = useState(false);
  const [total, setTotal] = useState(0);
  const [totalPages, setTotalPages] = useState(0);

  // ðŸ”¥ DEBOUNCE - espera 500ms apÃ³s parar de digitar
  const debouncedSearch = useCallback(
    (searchTerm) => {
      const timer = setTimeout(() => {
        setPage(1);
        load(searchTerm);
      }, 500);
      return () => clearTimeout(timer);
    },
    []
  );

  useEffect(() => {
    if (q !== '') {
      debouncedSearch(q);
    } else {
      // Se campo vazio, carrega normalmente
      setPage(1);
      load();
    }
  }, [q, debouncedSearch]);

  async function load(searchTerm = q) {
    setLoading(true);
    try {
      console.log('ðŸ”„ Carregando alunos...', { page, limit, q: searchTerm });
      const resp = await fetchStudents({ page, limit, q: searchTerm });
      console.log('ðŸ“¦ Resposta da API:', resp);
      setStudents(resp.data || []);
      setTotal(resp.total || 0);
      setTotalPages(resp.totalPages || 0);
    } catch (error) {
      console.error('âŒ Erro ao carregar alunos:', error);
      alert('Erro ao carregar dados');
    } finally {
      setLoading(false);
    }
  }

  // Recarregar quando mudar pÃ¡gina
  useEffect(() => { 
    if (q === '') {
      load();
    }
  }, [page]);

  // ðŸ”¥ FUNÃ‡ÃƒO DE BUSCA MANUAL (botÃ£o)
  function handleSearch() {
    setPage(1);
    load();
  }

  // ðŸ”¥ FUNÃ‡ÃƒO OPEN DETAIL (que estava faltando)
  async function openDetail(id) {
    try {
      const s = await getStudent(id);
      setSelected(s);
      setEditing(false);
    } catch (error) {
      console.error('Erro ao carregar detalhes:', error);
      alert('Erro ao carregar detalhes do aluno');
    }
  }

  // ðŸ”¥ FUNÃ‡ÃƒO SALVAR
  async function save() {
    if (!selected || !selected._id) return;
    try {
      const res = await updateStudent(selected._id, selected);
      setSelected(res);
      setEditing(false);
      load();
      alert('Aluno atualizado com sucesso!');
    } catch (error) {
      console.error('Erro ao salvar:', error);
      alert('Erro ao salvar alteraÃ§Ãµes');
    }
  }

  // ðŸ”¥ FUNÃ‡ÃƒO EXCLUIR
  async function remove(id) {
    if (!window.confirm('Tem certeza que deseja excluir este aluno?')) return;
    try {
      await deleteStudent(id);
      setSelected(null);
      load();
      alert('Aluno excluÃ­do com sucesso!');
    } catch (error) {
      console.error('Erro ao excluir:', error);
      alert('Erro ao excluir aluno');
    }
  }

  return (
    <div className="container">
      <header>
        <h1>Alunos NCA</h1>
        <div className="controls">
          <input 
            placeholder="Pesquisar por nome, CPF, turma, sÃ©rie ou localidade..." 
            value={q} 
            onChange={e => setQ(e.target.value)}
            onKeyPress={e => e.key === 'Enter' && handleSearch()}
          />
          <button onClick={handleSearch}>Buscar</button>
          {q && (
            <button 
              onClick={() => {
                setQ('');
                setPage(1);
              }}
              style={{ marginLeft: '8px', background: '#718096' }}
            >
              Limpar
            </button>
          )}
        </div>
      </header>

      <main>
        <section className="list">
          <div className="meta">
            <div>Total: {total}</div>
            {q && <div>Filtro: "{q}"</div>}
            <div>
              PÃ¡gina {page} de {totalPages} â€” 
              <button 
                disabled={page <= 1}
                onClick={() => setPage(p => Math.max(1, p - 1))}
              >â—€</button>
              <button 
                disabled={page >= totalPages}
                onClick={() => setPage(p => p + 1)}
              >â–¶</button>
            </div>
          </div>

          {loading ? <div>Carregando...</div> :
            students.length === 0 ? (
              <div style={{ padding: '20px', textAlign: 'center', color: '#718096' }}>
                {q ? `Nenhum aluno encontrado para "${q}"` : 'Nenhum aluno cadastrado'}
              </div>
            ) : (
              <table>
                <thead>
                  <tr>
                    <th>Nome</th><th>Data Nasc.</th><th>CPF</th><th>SÃ©rie</th><th>Turma</th><th>Localidade</th><th></th>
                  </tr>
                </thead>
                <tbody>
                  {students.map(s => (
                    <tr key={s._id || s.cpf}>
                      <td>{s.nome}</td>
                      <td>{s.dataNascimento ? new Date(s.dataNascimento).toLocaleDateString('pt-BR') : ''}</td>
                      <td>{s.cpf}</td>
                      <td>{s.serieAno}</td>
                      <td>{s.turma}</td>
                      <td>{s.localidade}</td>
                      <td><button onClick={() => openDetail(s._id)}>Abrir</button></td>
                    </tr>
                  ))}
                </tbody>
              </table>
            )
          }
        </section>

        <aside className="detail">
          {selected ? (
            <div>
              <h2>Detalhes do Aluno</h2>
              {!editing ? (
                <div className="read">
                  <div><strong>Nome:</strong> {selected.nome}</div>
                  <div><strong>CPF:</strong> {selected.cpf}</div>
                  <div><strong>CartÃ£o SUS:</strong> {selected.cartaoSUS}</div>
                  <div><strong>Data Nasc.:</strong> {selected.dataNascimento ? new Date(selected.dataNascimento).toLocaleDateString('pt-BR') : ''}</div>
                  <div><strong>SÃ©rie/Ano:</strong> {selected.serieAno}</div>
                  <div><strong>Turma:</strong> {selected.turma}</div>
                  <div><strong>Localidade:</strong> {selected.localidade}</div>
                  <div className="actions">
                    <button onClick={() => setEditing(true)}>Editar</button>
                    <button onClick={() => remove(selected._id)}>Excluir</button>
                    <button onClick={() => setSelected(null)}>Fechar</button>
                  </div>
                </div>
              ) : (
                <div className="form">
                  <label>Nome<input value={selected.nome || ''} onChange={e => setSelected({...selected, nome: e.target.value})} /></label>
                  <label>CPF<input value={selected.cpf || ''} onChange={e => setSelected({...selected, cpf: e.target.value})} /></label>
                  <label>Localidade<input value={selected.localidade || ''} onChange={e => setSelected({...selected, localidade: e.target.value})} /></label>
                  <div className="actions">
                    <button onClick={save}>Salvar</button>
                    <button onClick={() => setEditing(false)}>Cancelar</button>
                  </div>
                </div>
              )}
            </div>
          ) : <div>Selecione um aluno para ver detalhes</div>}
        </aside>
      </main>

      <footer>
        <small>API: http://localhost:4000/api/students</small>
      </footer>
    </div>
  );
}
