// server/index.js - BACKEND COMPLETO PARA MONGODB
const express = require('express');
const mongoose = require('mongoose');
const cors = require('cors');

const app = express();

// üî• MIDDLEWARES ESSENCIAIS
app.use(cors());
app.use(express.json());

// üî• CONEX√ÉO COM MONGODB
const MONGODB_URI = process.env.MONGODB_URI || 'mongodb://localhost:27017/escola_nca';
mongoose.connect(MONGODB_URI, {
  useNewUrlParser: true,
  useUnifiedTopology: true,
})
.then(() => console.log('‚úÖ Conectado ao MongoDB'))
.catch(err => console.error('‚ùå Erro ao conectar MongoDB:', err));

// üî• SCHEMA DO ALUNO
const alunoSchema = new mongoose.Schema({
  nome: { type: String, required: true },
  dataNascimento: Date,
  cpf: String,
  cartaoSUS: String,
  nomeMae: String,
  nomePai: String,
  serieAno: String,
  turma: String,
  turno: String,
  status: { type: String, default: 'Matriculado' },
  transporte: String,
  localidade: String,
  criadoEm: { type: Date, default: Date.now },
  atualizadoEm: { type: Date, default: Date.now }
});

const Aluno = mongoose.model('Aluno', alunoSchema);

// üî• ROTAS DA API

// GET /api/students - Listar alunos com pagina√ß√£o e busca
app.get('/api/students', async (req, res) => {
  try {
    const { page = 1, limit = 50, q = '' } = req.query;
    const skip = (page - 1) * limit;
    
    // Construir query de busca
    let query = {};
    if (q) {
      query = {
        $or: [
          { nome: { $regex: q, $options: 'i' } },
          { cpf: { $regex: q, $options: 'i' } },
          { serieAno: { $regex: q, $options: 'i' } },
          { turma: { $regex: q, $options: 'i' } },
          { localidade: { $regex: q, $options: 'i' } },
          { nomeMae: { $regex: q, $options: 'i' } },
          { nomePai: { $regex: q, $options: 'i' } }
        ]
      };
    }

    const [alunos, total] = await Promise.all([
      Aluno.find(query)
        .sort({ nome: 1 })
        .skip(skip)
        .limit(parseInt(limit))
        .lean(),
      Aluno.countDocuments(query)
    ]);

    res.json({
      success: true,
      data: alunos,
      total,
      totalPages: Math.ceil(total / limit),
      page: parseInt(page),
      limit: parseInt(limit)
    });
  } catch (error) {
    console.error('‚ùå Erro ao buscar alunos:', error);
    res.status(500).json({
      success: false,
      error: 'Erro interno do servidor'
    });
  }
});

// POST /api/students - Criar novo aluno
app.post('/api/students', async (req, res) => {
  try {
    console.log('üì• Dados recebidos para criar aluno:', req.body);
    
    const alunoData = {
      nome: req.body.nome?.trim(),
      dataNascimento: req.body.dataNascimento || null,
      cpf: req.body.cpf?.trim() || '',
      cartaoSUS: req.body.cartaoSUS?.trim() || '',
      nomeMae: req.body.nomeMae?.trim() || '',
      nomePai: req.body.nomePai?.trim() || '',
      serieAno: req.body.serieAno?.trim() || '',
      turma: req.body.turma?.trim() || '',
      turno: req.body.turno?.trim() || '',
      status: req.body.status?.trim() || 'Matriculado',
      transporte: req.body.transporte?.trim() || '',
      localidade: req.body.localidade?.trim() || '',
      atualizadoEm: new Date()
    };

    // Valida√ß√£o b√°sica
    if (!alunoData.nome) {
      return res.status(400).json({
        success: false,
        error: 'Nome do aluno √© obrigat√≥rio'
      });
    }

    const novoAluno = new Aluno(alunoData);
    const alunoSalvo = await novoAluno.save();

    console.log('‚úÖ Aluno criado com sucesso:', alunoSalvo._id);
    
    res.status(201).json({
      success: true,
      data: alunoSalvo,
      message: 'Aluno criado com sucesso!'
    });
  } catch (error) {
    console.error('‚ùå Erro ao criar aluno:', error);
    
    if (error.name === 'ValidationError') {
      return res.status(400).json({
        success: false,
        error: 'Dados de aluno inv√°lidos'
      });
    }
    
    res.status(500).json({
      success: false,
      error: 'Erro interno do servidor'
    });
  }
});

// GET /api/students/:id - Buscar aluno por ID
app.get('/api/students/:id', async (req, res) => {
  try {
    const aluno = await Aluno.findById(req.params.id);
    
    if (!aluno) {
      return res.status(404).json({
        success: false,
        error: 'Aluno n√£o encontrado'
      });
    }

    res.json({
      success: true,
      data: aluno
    });
  } catch (error) {
    console.error('‚ùå Erro ao buscar aluno:', error);
    res.status(500).json({
      success: false,
      error: 'Erro interno do servidor'
    });
  }
});

// PUT /api/students/:id - Atualizar aluno
app.put('/api/students/:id', async (req, res) => {
  try {
    const alunoAtualizado = await Aluno.findByIdAndUpdate(
      req.params.id,
      {
        ...req.body,
        atualizadoEm: new Date()
      },
      { new: true, runValidators: true }
    );

    if (!alunoAtualizado) {
      return res.status(404).json({
        success: false,
        error: 'Aluno n√£o encontrado'
      });
    }

    res.json({
      success: true,
      data: alunoAtualizado,
      message: 'Aluno atualizado com sucesso!'
    });
  } catch (error) {
    console.error('‚ùå Erro ao atualizar aluno:', error);
    res.status(500).json({
      success: false,
      error: 'Erro interno do servidor'
    });
  }
});

// DELETE /api/students/:id - Excluir aluno
app.delete('/api/students/:id', async (req, res) => {
  try {
    const alunoExcluido = await Aluno.findByIdAndDelete(req.params.id);

    if (!alunoExcluido) {
      return res.status(404).json({
        success: false,
        error: 'Aluno n√£o encontrado'
      });
    }

    res.json({
      success: true,
      message: 'Aluno exclu√≠do com sucesso!'
    });
  } catch (error) {
    console.error('‚ùå Erro ao excluir aluno:', error);
    res.status(500).json({
      success: false,
      error: 'Erro interno do servidor'
    });
  }
});

// üî• ROTA PARA IMPORTAR DADOS DO JSON
app.post('/api/importar-alunos', async (req, res) => {
  try {
    const alunosData = req.body;
    
    if (!Array.isArray(alunosData)) {
      return res.status(400).json({
        success: false,
        error: 'Dados devem ser um array de alunos'
      });
    }

    // Filtrar e formatar dados
    const alunosFormatados = alunosData
      .filter(aluno => aluno['Nome do Aluno'] && aluno['Nome do Aluno'].trim() !== '')
      .map(aluno => ({
        nome: aluno['Nome do Aluno'].trim(),
        dataNascimento: aluno['Data de Nasc.'] ? new Date(aluno['Data de Nasc.']) : null,
        cpf: aluno['CPF']?.toString().trim() || '',
        cartaoSUS: aluno['Cart√£o do SUS']?.toString().trim() || '',
        nomeMae: aluno['Nome M√£e']?.trim() || '',
        nomePai: aluno['Nome Pai']?.trim() || '',
        serieAno: aluno['S√©rie/Ano']?.toString().trim() || '',
        turma: aluno['Turma']?.toString().trim() || '',
        turno: aluno['Turno']?.trim() || '',
        status: aluno['Status']?.trim() || 'Matriculado',
        transporte: aluno['Transporte']?.trim() || '',
        localidade: aluno['Localidade']?.trim() || ''
      }));

    // Inserir no MongoDB
    const resultado = await Aluno.insertMany(alunosFormatados, { ordered: false });
    
    res.json({
      success: true,
      message: `${resultado.length} alunos importados com sucesso!`,
      data: resultado
    });
  } catch (error) {
    console.error('‚ùå Erro ao importar alunos:', error);
    res.status(500).json({
      success: false,
      error: 'Erro ao importar alunos'
    });
  }
});

