// POST /students — adicionar novo aluno
router.post('/', async (req, res) => {
  try {
    const student = new Student(req.body);
    await student.save();
    res.status(201).json({ message: 'Aluno criado com sucesso', student });
  } catch (err) {
    res.status(500).json({ error: err.message });
  }
});

// PUT /students/:id — editar aluno
router.put('/:id', async (req, res) => {
  try {
    const updated = await Student.findByIdAndUpdate(
      req.params.id,
      req.body,
      { new: true }
    );

    if (!updated) {
      return res.status(404).json({ message: 'Aluno não encontrado' });
    }

    res.json({ message: 'Aluno atualizado com sucesso', student: updated });
  } catch (err) {
    res.status(500).json({ error: err.message });
  }
});

// DELETE /students/:id — excluir aluno
router.delete('/:id', async (req, res) => {
  try {
    const deleted = await Student.findByIdAndDelete(req.params.id);

    if (!deleted) {
      return res.status(404).json({ message: 'Aluno não encontrado' });
    }

    res.json({ message: 'Aluno excluído com sucesso' });
  } catch (err) {
    res.status(500).json({ error: err.message });
  }
});
