import React, { useEffect, useState, useCallback } from 'react';
import { fetchStudents, getStudent, updateStudent, deleteStudent, createStudent } from './api';

// ðŸ”¥ COMPONENTES MOVIDOS PARA FORA DO COMPONENTE App

// Componente do Dashboard de EstatÃ­sticas
const StatsDashboard = ({ seriesStats, allStudents, dashboardLoading }) => {
  if (dashboardLoading) {
    return (
      <section className="stats-dashboard">
        <h3>ðŸ“Š DistribuiÃ§Ã£o por SÃ©rie/Turma</h3>
        <div className="stats-loading">Carregando estatÃ­sticas...</div>
      </section>
    );
  }

  if (Object.keys(seriesStats).length === 0 || !seriesStats.totalGeral) return null;
  
  // Remover totalGeral das sÃ©ries para ordenaÃ§Ã£o
  const { totalGeral, ...seriesData } = seriesStats;

  // Ordenar sÃ©ries numericamente
  const sortedSeries = Object.keys(seriesData).sort((a, b) => {
    if (a === 'NÃ£o informada') return 1;
    if (b === 'NÃ£o informada') return -1;
    const numA = parseInt(a) || 0;
    const numB = parseInt(b) || 0;
    return numA - numB;
  });

  return (
    <section className="stats-dashboard">
      <h3>ðŸ“Š DistribuiÃ§Ã£o por SÃ©rie/Turma - Total: {totalGeral || allStudents.length} alunos</h3>
      <div className="stats-grid">
        {sortedSeries.map(serie => (
          <div key={serie} className="stat-card">
            <div className="stat-header">
              <span className="stat-title">
                {serie === 'NÃ£o informada' ? 'â“' : 'ðŸ“š'} {serie}Âª SÃ©rie
              </span>
              <span className="stat-total">{seriesData[serie].count} alunos</span>
            </div>
            
            <div className="stat-turmas">
              {Object.entries(seriesData[serie].turmas)
                .sort(([turmaA], [turmaB]) => turmaA.localeCompare(turmaB))
                .map(([turma, count]) => (
                <div key={turma} className="turma-item">
                  <span className="turma-name">
                    {turma === 'Sem turma' ? 'ðŸ« Geral' : `Turma ${turma}`}
                  </span>
                  <span className="turma-count">{count}</span>
                </div>
              ))}
            </div>
          </div>
        ))}
        
        {/* Card de total geral */}
        <div className="stat-card total-card">
          <div className="stat-header">
            <span className="stat-title">ðŸ‘¥ Total Geral</span>
            <span className="stat-total">{totalGeral || allStudents.length} alunos</span>
          </div>
          <div className="stat-turmas">
            <div className="turma-item">
              <span className="turma-name">Todas as sÃ©ries</span>
              <span className="turma-count">{totalGeral || allStudents.length}</span>
            </div>
            <div className="turma-item">
              <span className="turma-name">SÃ©ries com dados</span>
              <span className="turma-count">{sortedSeries.length}</span>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

// FunÃ§Ã£o para obter Ã­cones baseados na localidade
const getLocalidadeIcon = (localidade) => {
  const lowerLocalidade = localidade.toLowerCase();
  
  if (lowerLocalidade.includes('goiabeira')) return 'ðŸŒ³';
  if (lowerLocalidade.includes('oiticica')) return 'ðŸŒ¿';
  if (lowerLocalidade.includes('centro')) return 'ðŸ¢';
  if (lowerLocalidade.includes('vila')) return 'ðŸ˜ï¸';
  if (lowerLocalidade.includes('bairro')) return 'ðŸ¡';
  if (lowerLocalidade.includes('rural')) return 'ðŸšœ';
  if (lowerLocalidade.includes('multirÃ£o')) return 'ðŸ‘¥';
  if (lowerLocalidade.includes('nÃ£o informada')) return 'â“';
  
  return 'ðŸ“';
};

// Componente do Dashboard de Localidades
const LocalidadeDashboard = ({ localidadeStats, seriesStats, allStudents, dashboardLoading }) => {
  if (dashboardLoading) {
    return (
      <section className="stats-dashboard localidade-dashboard">
        <h3>ðŸ˜ï¸ DistribuiÃ§Ã£o por Localidade</h3>
        <div className="stats-loading">Carregando estatÃ­sticas por localidade...</div>
      </section>
    );
  }

  if (Object.keys(localidadeStats).length === 0) return null;
    
  // Ordenar localidades por quantidade (maior primeiro)
  const sortedLocalidades = Object.keys(localidadeStats).sort((a, b) => {
    return localidadeStats[b].count - localidadeStats[a].count;
  });

  // Pegar as top localidades (mÃ¡ximo 8 para nÃ£o ficar muito grande)
  const topLocalidades = sortedLocalidades.slice(0, 8);

  return (
    <section className="stats-dashboard localidade-dashboard">
      <h3>ðŸ˜ï¸ DistribuiÃ§Ã£o por Localidade</h3>
      <div className="stats-grid">
        {topLocalidades.map(localidade => (
          <div key={localidade} className="stat-card localidade-card">
            <div className="stat-header">
              <span className="stat-title">
                {getLocalidadeIcon(localidade)} {localidade}
              </span>
              <span className="stat-total">{localidadeStats[localidade].count} alunos</span>
            </div>
            
            <div className="stat-turmas">
              {Object.entries(localidadeStats[localidade].series)
                .sort(([serieA], [serieB]) => {
                  if (serieA === 'NÃ£o informada') return 1;
                  if (serieB === 'NÃ£o informada') return -1;
                  const numA = parseInt(serieA) || 0;
                  const numB = parseInt(serieB) || 0;
                  return numA - numB;
                })
                .slice(0, 3) // Mostrar apenas top 3 sÃ©ries
                .map(([serie, count]) => (
                <div key={serie} className="turma-item">
                  <span className="turma-name">
                    {serie === 'NÃ£o informada' ? 'ðŸ“š Geral' : `${serie}Âª SÃ©rie`}
                  </span>
                  <span className="turma-count">{count}</span>
                </div>
              ))}
              
              {Object.keys(localidadeStats[localidade].series).length > 3 && (
                <div className="turma-item more-items">
                  <span className="turma-name">+ {Object.keys(localidadeStats[localidade].series).length - 3} sÃ©ries</span>
                </div>
              )}
            </div>
          </div>
        ))}
        
        {/* Card de resumo das localidades */}
        <div className="stat-card total-card localidade-total">
          <div className="stat-header">
            <span className="stat-title">ðŸ—ºï¸ Resumo Localidades</span>
            <span className="stat-total">{sortedLocalidades.length} locais</span>
          </div>
          <div className="stat-turmas">
            <div className="turma-item">
              <span className="turma-name">Total de alunos</span>
              <span className="turma-count">{seriesStats.totalGeral || allStudents.length}</span>
            </div>
            <div className="turma-item">
              <span className="turma-name">Localidades com dados</span>
              <span className="turma-count">{sortedLocalidades.length}</span>
            </div>
            <div className="turma-item">
              <span className="turma-name">Maior localidade</span>
              <span className="turma-count">
                {sortedLocalidades[0] ? `${localidadeStats[sortedLocalidades[0]].count} alunos` : '-'}
              </span>
            </div>
          </div>
        </div>
      </div>
      
      {/* GrÃ¡fico de barras simples para visualizaÃ§Ã£o */}
      {sortedLocalidades.length > 0 && (
        <div className="localidade-chart">
          <h4>ðŸ“ˆ Top Localidades por Quantidade de Alunos</h4>
          <div className="chart-bars">
            {topLocalidades.map(localidade => {
              const count = localidadeStats[localidade].count;
              const maxCount = localidadeStats[topLocalidades[0]].count;
              const percentage = (count / maxCount) * 100;
              
              return (
                <div key={localidade} className="chart-bar">
                  <div className="chart-label">
                    <span className="localidade-name">{localidade}</span>
                    <span className="localidade-count">{count}</span>
                  </div>
                  <div className="chart-bar-track">
                    <div 
                      className="chart-bar-fill"
                      style={{ width: `${percentage}%` }}
                    ></div>
                  </div>
                </div>
              );
            })}
          </div>
        </div>
      )}
    </section>
  );
};

// ðŸ”¥ COMPONENTE App PRINCIPAL
export default function App() {
  const [students, setStudents] = useState([]);
  const [allStudents, setAllStudents] = useState([]);
  const [q, setQ] = useState('');
  const [page, setPage] = useState(1);
  const [limit] = useState(50);
  const [loading, setLoading] = useState(false);
  const [selected, setSelected] = useState(null);
  const [editing, setEditing] = useState(false);
  const [creating, setCreating] = useState(false);
  const [total, setTotal] = useState(0);
  const [totalPages, setTotalPages] = useState(0);
  const [seriesStats, setSeriesStats] = useState({});
  const [localidadeStats, setLocalidadeStats] = useState({});
  const [dashboardLoading, setDashboardLoading] = useState(false);

  // Aluno vazio para criaÃ§Ã£o
  const emptyStudent = {
    nome: '',
    dataNascimento: '',
    cpf: '',
    cartaoSUS: '',
    serieAno: '',
    turma: '',
    turno: '',
    status: '',
    transporte: '',
    localidade: ''
  };

  // ðŸ”¥ NOVO: Carregar TODOS os alunos para o dashboard
  const loadAllStudents = async () => {
    setDashboardLoading(true);
    try {
      const resp = await fetchStudents({ page: 1, limit: 1000, q: '' });
      setAllStudents(resp.data || []);
    } catch (error) {
      console.error('Erro ao carregar dados do dashboard:', error);
    } finally {
      setDashboardLoading(false);
    }
  };

  // Carregar alunos (filtrados)
  async function load(searchTerm = q) {
    setLoading(true);
    try {
      const resp = await fetchStudents({ page, limit, q: searchTerm });
      setStudents(resp.data || []);
      setTotal(resp.total || 0);
      setTotalPages(resp.totalPages || 0);
    } catch (error) {
      console.error('Erro ao carregar alunos:', error);
      alert('Erro ao carregar dados');
    } finally {
      setLoading(false);
    }
  }

  // ðŸ”¥ ATUALIZADO: Calcular estatÃ­sticas com TODOS os alunos
  useEffect(() => {
    if (allStudents.length > 0) {
      const statsSeries = {};
      const statsLocalidades = {};
      let totalGeral = 0;
      
      allStudents.forEach(student => {
        const serie = student.serieAno?.toString().trim() || 'NÃ£o informada';
        const turma = student.turma?.toString().trim() || 'Sem turma';
        const localidade = student.localidade?.toString().trim() || 'NÃ£o informada';
        
        totalGeral++;
        
        // EstatÃ­stica por sÃ©rie
        if (!statsSeries[serie]) {
          statsSeries[serie] = {
            count: 0,
            turmas: {}
          };
        }
        statsSeries[serie].count++;
        
        if (!statsSeries[serie].turmas[turma]) {
          statsSeries[serie].turmas[turma] = 0;
        }
        statsSeries[serie].turmas[turma]++;
        
        // ðŸ”¥ NOVO: EstatÃ­stica por localidade
        if (!statsLocalidades[localidade]) {
          statsLocalidades[localidade] = {
            count: 0,
            series: {}
          };
        }
        statsLocalidades[localidade].count++;
        
        // EstatÃ­stica por sÃ©rie dentro da localidade
        if (!statsLocalidades[localidade].series[serie]) {
          statsLocalidades[localidade].series[serie] = 0;
        }
        statsLocalidades[localidade].series[serie]++;
      });
      
      statsSeries.totalGeral = totalGeral;
      setSeriesStats(statsSeries);
      setLocalidadeStats(statsLocalidades);
    } else {
      setSeriesStats({});
      setLocalidadeStats({});
    }
  }, [allStudents]);

  // ðŸ”¥ NOVO: Carregar dados do dashboard na inicializaÃ§Ã£o
  useEffect(() => {
    loadAllStudents();
    load();
  }, []);

  // Debounce para pesquisa
  const debouncedSearch = useCallback((searchTerm) => {
    const timer = setTimeout(() => {
      setPage(1);
      load(searchTerm);
    }, 500);
    return () => clearTimeout(timer);
  }, []);

  useEffect(() => {
    if (q !== '') {
      debouncedSearch(q);
    } else {
      setPage(1);
      load();
    }
  }, [q, debouncedSearch]);

  // Recarregar quando mudar pÃ¡gina
  useEffect(() => { 
    if (q === '') {
      load();
    }
  }, [page]);

  // ðŸ”¥ ATUALIZADO: Recarregar dashboard apÃ³s criar/editar/excluir
  const handleStudentChange = () => {
    loadAllStudents();
    load();
  };

  // Busca manual
  function handleSearch() {
    setPage(1);
    load();
  }

  // Abrir detalhes do aluno
  async function openDetail(id) {
    try {
      const s = await getStudent(id);
      setSelected(s);
      setEditing(false);
      setCreating(false);
    } catch (error) {
      console.error('Erro ao carregar detalhes:', error);
      alert('Erro ao carregar detalhes do aluno');
    }
  }

  // ðŸ”¥ ATUALIZADO: Salvar ediÃ§Ã£o
  async function save() {
    if (!selected || !selected._id) return;
    try {
      const res = await updateStudent(selected._id, selected);
      setSelected(res);
      setEditing(false);
      handleStudentChange();
      alert('Aluno atualizado com sucesso!');
    } catch (error) {
      console.error('Erro ao salvar:', error);
      alert('Erro ao salvar alteraÃ§Ãµes');
    }
  }

  // ðŸ”¥ ATUALIZADO: Excluir aluno
  async function remove(id) {
    if (!window.confirm('Tem certeza que deseja excluir este aluno?')) return;
    try {
      await deleteStudent(id);
      setSelected(null);
      handleStudentChange();
      alert('Aluno excluÃ­do com sucesso!');
    } catch (error) {
      console.error('Erro ao excluir:', error);
      alert('Erro ao excluir aluno');
    }
  }

  // ðŸ”¥ ATUALIZADO: Criar novo aluno
  async function createNewStudent() {
    try {
      await createStudent(selected);
      alert('Aluno criado com sucesso!');
      setCreating(false);
      setSelected(null);
      handleStudentChange();
    } catch (error) {
      console.error('Erro ao criar aluno:', error);
      alert('Erro ao criar aluno');
    }
  }

  // Iniciar criaÃ§Ã£o
  function startCreate() {
    setSelected({...emptyStudent});
    setCreating(true);
    setEditing(false);
  }

  // Cancelar criaÃ§Ã£o
  function cancelCreate() {
    setCreating(false);
    setSelected(null);
  }

  return (
    <div className="container">
      <header>
        <h1>Alunos NCA</h1>
        <div className="controls">
          <input 
            placeholder="Buscar por: nome, CPF, sÃ©rie, turma..." 
            value={q} 
            onChange={e => setQ(e.target.value)}
            onKeyPress={e => e.key === 'Enter' && handleSearch()}
          />
          <button onClick={handleSearch}>Buscar</button>
          
          <button 
            onClick={startCreate}
            style={{ background: '#38a169', marginLeft: '8px' }}
          >
            + Novo Aluno
          </button>
          
          {q && (
            <button 
              onClick={() => {
                setQ('');
                setPage(1);
              }}
              style={{ marginLeft: '8px', background: '#718096' }}
            >
              Limpar
            </button>
          )}
        </div>
        
        {!q && (
          <div className="search-tips">
            <small>
              ðŸ’¡ <strong>Dicas:</strong> 
              "2" (sÃ©rie) â€¢ "A" (turma) â€¢ "09565384366" (CPF) â€¢ "Goiabeira" (localidade) â€¢ "Ana" (nome)
            </small>
          </div>
        )}
      </header>

      {/* ðŸ”¥ DASHBOARDS COM PROPS CORRETAS */}
      <StatsDashboard 
        seriesStats={seriesStats}
        allStudents={allStudents}
        dashboardLoading={dashboardLoading}
      />

      <LocalidadeDashboard 
        localidadeStats={localidadeStats}
        seriesStats={seriesStats}
        allStudents={allStudents}
        dashboardLoading={dashboardLoading}
      />

      {/* ðŸ”¥ PAINEL DE DETALHES COMPLETO */}
      <section className="detail-panel">
        {selected ? (
          <div className="detail-content">
            <h2>
              {creating ? 'Novo Aluno' : editing ? 'Editando Aluno' : 'Detalhes do Aluno'}
            </h2>
            
            {editing || creating ? (
              <div className="form">
                <label>
                  Nome:
                  <input 
                    value={selected.nome || ''} 
                    onChange={e => setSelected({...selected, nome: e.target.value})}
                  />
                </label>
                <label>
                  Data Nascimento:
                  <input 
                    type="date"
                    value={selected.dataNascimento ? new Date(selected.dataNascimento).toISOString().split('T')[0] : ''} 
                    onChange={e => setSelected({...selected, dataNascimento: e.target.value})}
                  />
                </label>
                <label>
                  CPF:
                  <input 
                    value={selected.cpf || ''} 
                    onChange={e => setSelected({...selected, cpf: e.target.value})}
                  />
                </label>
                <label>
                  SÃ©rie/Ano:
                  <input 
                    value={selected.serieAno || ''} 
                    onChange={e => setSelected({...selected, serieAno: e.target.value})}
                  />
                </label>
                <label>
                  Turma:
                  <input 
                    value={selected.turma || ''} 
                    onChange={e => setSelected({...selected, turma: e.target.value})}
                  />
                </label>
                <label>
                  Localidade:
                  <input 
                    value={selected.localidade || ''} 
                    onChange={e => setSelected({...selected, localidade: e.target.value})}
                  />
                </label>
                
                <div className="actions">
                  {creating ? (
                    <>
                      <button onClick={createNewStudent}>Criar Aluno</button>
                      <button onClick={cancelCreate}>Cancelar</button>
                    </>
                  ) : (
                    <>
                      <button onClick={save}>Salvar</button>
                      <button onClick={() => setEditing(false)}>Cancelar</button>
                    </>
                  )}
                </div>
              </div>
            ) : (
              <div className="read">
                <div><strong>Nome:</strong> {selected.nome}</div>
                <div><strong>Data Nasc.:</strong> {selected.dataNascimento ? new Date(selected.dataNascimento).toLocaleDateString('pt-BR') : ''}</div>
                <div><strong>CPF:</strong> {selected.cpf}</div>
                <div><strong>SÃ©rie/Ano:</strong> {selected.serieAno}</div>
                <div><strong>Turma:</strong> {selected.turma}</div>
                <div><strong>Localidade:</strong> {selected.localidade}</div>
                
                <div className="actions">
                  <button onClick={() => setEditing(true)}>Editar</button>
                  <button onClick={() => remove(selected._id)} style={{background: '#e53e3e'}}>
                    Excluir
                  </button>
                  <button onClick={() => setSelected(null)}>Fechar</button>
                </div>
              </div>
            )}
          </div>
        ) : (
          <div className="empty-detail">
            {creating ? 'Preencha os dados do novo aluno' : 'Selecione um aluno para ver os detalhes'}
          </div>
        )}
      </section>

      <main>
        <section className="list">
          <div className="meta">
            <div>Total na busca: {total}</div>
            {q && <div>Filtro: "{q}"</div>}
            <div>
              PÃ¡gina {page} de {totalPages} â€” 
              <button 
                disabled={page <= 1}
                onClick={() => setPage(p => Math.max(1, p - 1))}
              >â—€</button>
              <button 
                disabled={page >= totalPages}
                onClick={() => setPage(p => p + 1)}
              >â–¶</button>
            </div>
          </div>

          {loading ? (
            <div>Carregando...</div>
          ) : students.length === 0 ? (
            <div style={{ padding: '20px', textAlign: 'center', color: '#718096' }}>
              {q ? `Nenhum aluno encontrado para "${q}"` : 'Nenhum aluno cadastrado'}
            </div>
          ) : (
            <table>
              <thead>
                <tr>
                  <th>Nome</th>
                  <th>Data Nasc.</th>
                  <th>CPF</th>
                  <th>SÃ©rie</th>
                  <th>Turma</th>
                  <th>Localidade</th>
                  <th></th>
                </tr>
              </thead>
              <tbody>
                {students.map(s => (
                  <tr key={s._id || s.cpf}>
                    <td>{s.nome}</td>
                    <td>{s.dataNascimento ? new Date(s.dataNascimento).toLocaleDateString('pt-BR') : ''}</td>
                    <td>{s.cpf}</td>
                    <td>{s.serieAno}</td>
                    <td>{s.turma}</td>
                    <td>{s.localidade}</td>
                    <td>
                      <button onClick={() => openDetail(s._id)}>Abrir</button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          )}
        </section>
      </main>

      <footer>
        <small>API: http://localhost:4000/api/students</small>
      </footer>
    </div>
  );
}
