import React, { useState, useRef } from 'react';
import jsPDF from 'jspdf';
import './DeclaracaoModal.css';

// Importe a imagem de background - ajuste o caminho conforme necess√°rio
import backgroundImage from '../img/background.png';

const DeclaracaoModal = ({ aluno, onClose }) => {
  const [tipoDeclaracao, setTipoDeclaracao] = useState('vaga');
  const [mostrarPreview, setMostrarPreview] = useState(false);
  const [pdfUrl, setPdfUrl] = useState('');
  const previewRef = useRef(null);

  const tiposDeclaracao = {
    vaga: {
      nome: 'Declara√ß√£o de Vaga Dispon√≠vel',
      template: (aluno) => {
        const dataNasc = aluno.dataNascimento ? new Date(aluno.dataNascimento).toLocaleDateString('pt-BR') : '______/______/______';
        const serie = aluno.serieAno || '____';
        const turma = aluno.turma || '';
        
        return `Declaramos, para os devidos fins de prova e efeitos legais, que temos vaga no ${serie}¬∫ ano ${turma}, turnos manh√£ e tarde, para o(a) aluno(a) ${aluno.nome.toUpperCase()}, nascido(a) em ${dataNasc}.`;
      },
      adicional: `O referido √© verdade e dou f√©.`
    },
    matricula: {
      nome: 'Declara√ß√£o de Matr√≠cula',
      template: (aluno) => {
        const dataNasc = aluno.dataNascimento ? new Date(aluno.dataNascimento).toLocaleDateString('pt-BR') : '______/______/______';
        const serie = aluno.serieAno || '____';
        const turma = aluno.turma || '';
        
        return `Declaramos, para os devidos fins, que ${aluno.nome}, nascido(a) em ${dataNasc}, encontra-se regularmente matriculado(a) no ${serie}¬∫ ano ${turma} do Ensino Fundamental ${parseInt(serie) <= 5 ? 'I' : 'II'} desta institui√ß√£o de ensino.`;
      },
      adicional: `Documento v√°lido para comprova√ß√£o de matr√≠cula junto aos √≥rg√£os competentes.`
