    transferencia: {
      nome: 'Declara√ß√£o de Transfer√™ncia',
      template: (aluno) => {
        const dataNasc = aluno.dataNascimento ? new Date(aluno.dataNascimento).toLocaleDateString('pt-BR') : '______/______/______';
        const serie = aluno.serieAno || '____';
        const turma = aluno.turma || '';
        
        return `Declaramos que ${aluno.nome}, nascido(a) em ${dataNasc}, est√° regularmente matriculado(a) no ${serie}¬∫ ano ${turma} e solicita transfer√™ncia para outra institui√ß√£o de ensino.`;
      },
      adicional: `Atestamos a regularidade de sua situa√ß√£o escolar para fins de transfer√™ncia.`
    },
    frequencia: {
      nome: 'Declara√ß√£o de Frequ√™ncia',
      template: (aluno) => {
        const serie = aluno.serieAno || '____';
        const turma = aluno.turma || '';
        const turno = aluno.turno || '_______';
        
        return `Certificamos que ${aluno.nome} frequenta regularmente as aulas no ${serie}¬∫ ano ${turma}, turno ${turno}, desta institui√ß√£o de ensino, estando em dia com suas obriga√ß√µes escolares.`;
      },
      adicional: `Documento v√°lido para comprova√ß√£o de frequ√™ncia escolar.`
    }
  };

  // Fun√ß√£o para adicionar background ao PDF
  const addBackgroundToPDF = (doc) => {
    // Carrega a imagem de background e a adiciona ao PDF
    doc.addImage(backgroundImage, 'PNG', 0, 0, doc.internal.pageSize.getWidth(), doc.internal.pageSize.getHeight());
  };

  const gerarPDF = (download = false) => {
    const doc = new jsPDF("portrait", "pt", "a4");
    const largura = doc.internal.pageSize.getWidth();
    const altura = doc.internal.pageSize.getHeight();

    // üî• ADICIONA O BACKGROUND PERSONALIZADO
    addBackgroundToPDF(doc);

    // Configura√ß√µes de fonte e cor para texto sobre o background
    doc.setFont("Times", "Normal");
    doc.setFontSize(11);
    doc.setTextColor(0, 0, 0); // Preto para melhor contraste

    // Texto principal da declara√ß√£o (posicionado abaixo do cabe√ßalho do background)
    const template = tiposDeclaracao[tipoDeclaracao].template(aluno);
    const linhas = doc.splitTextToSize(template, largura - 100); // Margens laterais
    
    // Posi√ß√£o Y ajustada para ficar abaixo do cabe√ßalho do background
    let posicaoY = 280;
    
    // Adiciona o texto principal
    linhas.forEach(linha => {
