    doc.setTextColor(0, 0, 0);
    doc.text(`Pr√©-visualiza√ß√£o: ${tiposDeclaracao[tipoDeclaracao].nome}`, 50, 50);
    doc.text(`Aluno: ${aluno.nome}`, 50, 70);
