    const corpo = `Segue em anexo a declara√ß√£o de ${tiposDeclaracao[tipoDeclaracao].nome} para ${aluno.nome}.`;
    const mailto = `mailto:?subject=${encodeURIComponent(assunto)}&body=${encodeURIComponent(corpo)}`;
    window.location.href = mailto;
  };

  if (!aluno) return null;

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        {!mostrarPreview ? (
          <>
            <h2>üìÑ Gerar Declara√ß√£o com Background Oficial</h2>
            
            <div className="aluno-info">
              <h3>Aluno: {aluno.nome}</h3>
              <p>S√©rie/Turma: {aluno.serieAno}¬∫ ano {aluno.turma}</p>
              {aluno.dataNascimento && (
                <p>Nascimento: {new Date(aluno.dataNascimento).toLocaleDateString('pt-BR')}</p>
              )}
            </div>

            <div className="tipo-selecao">
              <label>Tipo de Declara√ß√£o:</label>
              <select 
                value={tipoDeclaracao} 
                onChange={(e) => setTipoDeclaracao(e.target.value)}
              >
                <option value="vaga">‚úÖ Vaga Dispon√≠vel</option>
                <option value="matricula">üìù Matr√≠cula</option>
                <option value="transferencia">üîÑ Transfer√™ncia</option>
                <option value="frequencia">üìä Frequ√™ncia</option>
              </select>
            </div>

            <div className="preview-texto">
              <h4>üìã Texto da Declara√ß√£o:</h4>
              <div className="texto-preview">
                <div className="background-notice">
                  <strong>‚ö†Ô∏è Background Oficial Inclu√≠do:</strong> 
                  Cabe√ßalho completo, rodap√© com assinatura da diretora "Volt√≠lia Maria Costa" e informa√ß√µes institucionais.
                </div>
                {tiposDeclaracao[tipoDeclaracao].template(aluno).split('\n').map((linha, i) => (
                  <p key={i}>{linha}</p>
                ))}
                <p className="texto-adicional">{tiposDeclaracao[tipoDeclaracao].adicional}</p>
              </div>
            </div>

            <div className="background-preview">
              <h4>üé® Visualiza√ß√£o do Background:</h4>
              <div className="background-image">
                <img 
                  src={backgroundImage} 
                  alt="Background Oficial da Declara√ß√£o" 
                  style={{maxWidth: '100%', border: '1px solid #ddd', borderRadius: '4px'}}
                />
                <div className="background-info">
                  <small>Modelo oficial com cabe√ßalho institucional e assinatura da diretora</small>
                </div>
              </div>
            </div>

            <div className="modal-actions">
              <button 
                className="btn-preview"
                onClick={() => gerarPDF(false)}
              >
                üëÅÔ∏è Ver Preview Completo
              </button>
              <button 
                className="btn-download"
                onClick={() => gerarPDF(true)}
              >
                ‚¨áÔ∏è Baixar PDF com Background
              </button>
              <button className="btn-cancel" onClick={onClose}>
                ‚ùå Cancelar
              </button>
            </div>
          </>
        ) : (
          <>
            <h2>üëÅÔ∏è Preview da Declara√ß√£o - Background Oficial</h2>
            
            <div className="pdf-preview">
              <iframe 
