import React, { useState, useRef } from 'react';
import jsPDF from 'jspdf';
import './DeclaracaoModal.css';

// üî• CORRE√á√ÉO: Use caminho p√∫blico
const backgroundImage = '/img/background.png';

const DeclaracaoModal = ({ aluno, onClose }) => {
  const [tipoDeclaracao, setTipoDeclaracao] = useState('vaga');
  const [pdfUrl, setPdfUrl] = useState('');
  const [pdfGerado, setPdfGerado] = useState(false);
  const previewRef = useRef(null);

  const tiposDeclaracao = {
    vaga: {
      nome: 'Declara√ß√£o de Vaga Dispon√≠vel',
      template: (aluno) => {
        const dataNasc = aluno.dataNascimento ? new Date(aluno.dataNascimento).toLocaleDateString('pt-BR') : '______/______/______';
        const serie = aluno.serieAno || '____';
        const turma = aluno.turma || '';
        
        return `Declaramos, para os devidos fins de prova e efeitos legais, que temos vaga no ${serie}¬∫ ano ${turma}, turnos manh√£ e tarde, para o(a) aluno(a) ${aluno.nome.toUpperCase()}, nascido(a) em ${dataNasc}.`;
      },
      adicional: `O referido √© verdade e dou f√©.`
    },
    matricula: {
      nome: 'Declara√ß√£o de Matr√≠cula',
      template: (aluno) => {
        const dataNasc = aluno.dataNascimento ? new Date(aluno.dataNascimento).toLocaleDateString('pt-BR') : '______/______/______';
        const serie = aluno.serieAno || '____';
        const turma = aluno.turma || '';
        
        return `Declaramos, para os devidos fins, que ${aluno.nome}, nascido(a) em ${dataNasc}, encontra-se regularmente matriculado(a) no ${serie}¬∫ ano ${turma} do Ensino Fundamental ${parseInt(serie) <= 5 ? 'I' : 'II'} desta institui√ß√£o de ensino.`;
      },
      adicional: `Documento v√°lido para comprova√ß√£o de matr√≠cula junto aos √≥rg√£os competentes.`
    },
    transferencia: {
      nome: 'Declara√ß√£o de Transfer√™ncia',
      template: (aluno) => {
        const dataNasc = aluno.dataNascimento ? new Date(aluno.dataNascimento).toLocaleDateString('pt-BR') : '______/______/______';
        const serie = aluno.serieAno || '____';
        const turma = aluno.turma || '';
        
        return `Declaramos que ${aluno.nome}, nascido(a) em ${dataNasc}, est√° regularmente matriculado(a) no ${serie}¬∫ ano ${turma} e solicita transfer√™ncia para outra institui√ß√£o de ensino.`;
      },
      adicional: `Atestamos a regularidade de sua situa√ß√£o escolar para fins de transfer√™ncia.`
    },
    frequencia: {
      nome: 'Declara√ß√£o de Frequ√™ncia',
      template: (aluno) => {
        const serie = aluno.serieAno || '____';
        const turma = aluno.turma || '';
        const turno = aluno.turno || '_______';
        
        return `Certificamos que ${aluno.nome} frequenta regularmente as aulas no ${serie}¬∫ ano ${turma}, turno ${turno}, desta institui√ß√£o de ensino, estando em dia com suas obriga√ß√µes escolares.`;
      },
      adicional: `Documento v√°lido para comprova√ß√£o de frequ√™ncia escolar.`
    }
  };

  // Fun√ß√£o para carregar a imagem e adicionar ao PDF
  const addBackgroundToPDF = (doc) => {
    return new Promise((resolve) => {
      const img = new Image();
      img.onload = () => {
        doc.addImage(img, 'PNG', 0, 0, doc.internal.pageSize.getWidth(), doc.internal.pageSize.getHeight());
        resolve();
      };
      img.onerror = () => {
        console.warn('Background n√£o carregado, gerando declara√ß√£o sem background');
        resolve();
      };
      img.src = backgroundImage;
    });
  };

  const gerarPDF = async (download = false) => {
    const doc = new jsPDF("portrait", "pt", "a4");
    const largura = doc.internal.pageSize.getWidth();
    const altura = doc.internal.pageSize.getHeight();

    try {
      // üî• ADICIONA O BACKGROUND PERSONALIZADO
      await addBackgroundToPDF(doc);
    } catch (error) {
      console.warn('Erro ao carregar background:', error);
    }

    // Configura√ß√µes de fonte e cor para texto sobre o background
    doc.setFont("Times", "Normal");
    doc.setFontSize(11);
    doc.setTextColor(0, 0, 0); // Preto para melhor contraste

    // Texto principal da declara√ß√£o (posicionado abaixo do cabe√ßalho do background)
    const template = tiposDeclaracao[tipoDeclaracao].template(aluno);
    const linhas = doc.splitTextToSize(template, largura - 100); // Margens laterais
    
    // Posi√ß√£o Y ajustada para ficar abaixo do cabe√ßalho do background
    let posicaoY = 280;
    
    // Adiciona o texto principal
    linhas.forEach(linha => {
      doc.text(linha, 50, posicaoY);
      posicaoY += 20;
    });

    // Texto adicional espec√≠fico do tipo de declara√ß√£o
    posicaoY += 20;
    const adicional = tiposDeclaracao[tipoDeclaracao].adicional;
    const linhasAdicional = doc.splitTextToSize(adicional, largura - 100);
    
    linhasAdicional.forEach(linha => {
      doc.text(linha, 50, posicaoY);
      posicaoY += 20;
    });

    // Data e local (posicionado acima da assinatura do background)
    const hoje = new Date().toLocaleDateString('pt-BR');
    doc.text(`Oiticica, Frecheirinha/CE, ${hoje}.`, 50, 650);

    if (download) {
      doc.save(`Declaracao_${tipoDeclaracao}_${aluno.nome.replace(/\s+/g, '_')}.pdf`);
      alert('‚úÖ Declara√ß√£o baixada com sucesso!');
    } else {
      // Criar URL para preview
      const pdfBlob = doc.output('blob');
      const url = URL.createObjectURL(pdfBlob);
      setPdfUrl(url);
      setPdfGerado(true);
    }
  };

  // üî• COMPARTILHAMENTO DIRETO - SISTEMA ANTERIOR
  const compartilharWhatsApp = () => {
    if (!pdfGerado) {
      alert('‚ö†Ô∏è Gere a declara√ß√£o primeiro antes de compartilhar!');
      return;
    }

    const texto = `üìÑ *Declara√ß√£o Escolar - ${tiposDeclaracao[tipoDeclaracao].nome}*\n\n` +
                 `üë§ *Aluno:* ${aluno.nome}\n` +
                 `üìö *S√©rie/Turma:* ${aluno.serieAno}¬∫ ano ${aluno.turma}\n` +
                 `üè´ *Escola:* E.E.F. Nair Cunha de Aguiar\n\n` +
                 `_Declara√ß√£o gerada em ${new Date().toLocaleDateString('pt-BR')}_`;

    const url = `https://wa.me/?text=${encodeURIComponent(texto)}`;
    window.open(url, '_blank');
  };

  const compartilharEmail = () => {
    if (!pdfGerado) {
      alert('‚ö†Ô∏è Gere a declara√ß√£o primeiro antes de compartilhar!');
      return;
    }

    const assunto = `üìÑ Declara√ß√£o Escolar - ${aluno.nome}`;
    const corpo = `Prezado(a),\n\nSegue em anexo a declara√ß√£o escolar solicitada:\n\n` +
                  `üî∏ *Tipo:* ${tiposDeclaracao[tipoDeclaracao].nome}\n` +
                  `üî∏ *Aluno:* ${aluno.nome}\n` +
                  `üî∏ *S√©rie/Turma:* ${aluno.serieAno}¬∫ ano ${aluno.turma}\n` +
                  `üî∏ *Data de Emiss√£o:* ${new Date().toLocaleDateString('pt-BR')}\n\n` +
                  `Atenciosamente,\nE.E.F. Nair Cunha de Aguiar\nS√≠tio Oiticica, Frecheirinha-CE`;

    const mailto = `mailto:?subject=${encodeURIComponent(assunto)}&body=${encodeURIComponent(corpo)}`;
    window.location.href = mailto;
  };

  const baixarPDF = () => {
    if (!pdfGerado) {
      alert('‚ö†Ô∏è Gere a declara√ß√£o primeiro antes de baixar!');
      return;
    }
    
    const link = document.createElement('a');
    link.href = pdfUrl;
    link.download = `Declaracao_${tipoDeclaracao}_${aluno.nome.replace(/\s+/g, '_')}.pdf`;
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
    alert('‚úÖ Declara√ß√£o baixada com sucesso!');
  };

  const imprimirPDF = () => {
    if (!pdfGerado) {
      alert('‚ö†Ô∏è Gere a declara√ß√£o primeiro antes de imprimir!');
      return;
    }

    const iframe = previewRef.current;
    if (iframe && iframe.contentWindow) {
      iframe.contentWindow.print();
    }
  };

  if (!aluno) return null;

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <h2>üìÑ Gerar Declara√ß√£o com Background Oficial</h2>
        
        <div className="aluno-info">
          <h3>üë§ Aluno: {aluno.nome}</h3>
          <p>üìö S√©rie/Turma: {aluno.serieAno}¬∫ ano {aluno.turma}</p>
          {aluno.dataNascimento && (
            <p>üéÇ Nascimento: {new Date(aluno.dataNascimento).toLocaleDateString('pt-BR')}</p>
          )}
        </div>

        <div className="tipo-selecao">
          <label>üìã Tipo de Declara√ß√£o:</label>
          <select 
            value={tipoDeclaracao} 
            onChange={(e) => {
              setTipoDeclaracao(e.target.value);
              setPdfGerado(false); // Reseta o PDF quando mudar o tipo
            }}
          >
            <option value="vaga">‚úÖ Vaga Dispon√≠vel</option>
            <option value="matricula">üìù Matr√≠cula</option>
            <option value="transferencia">üîÑ Transfer√™ncia</option>
            <option value="frequencia">üìä Frequ√™ncia</option>
          </select>
        </div>

        <div className="preview-texto">
          <h4>üìù Texto da Declara√ß√£o:</h4>
          <div className="texto-preview">
            <div className="background-notice">
              <strong>üé® Background Oficial Inclu√≠do:</strong> 
              Cabe√ßalho completo, rodap√© com assinatura da diretora "Volt√≠lia Maria Costa" e informa√ß√µes institucionais.
            </div>
            {tiposDeclaracao[tipoDeclaracao].template(aluno).split('\n').map((linha, i) => (
              <p key={i}>{linha}</p>
            ))}
            <p className="texto-adicional">{tiposDeclaracao[tipoDeclaracao].adicional}</p>
          </div>
        </div>

        <div className="background-preview">
          <h4>üè´ Visualiza√ß√£o do Background:</h4>
          <div className="background-image">
            <img 
              src={backgroundImage} 
              alt="Background Oficial da Declara√ß√£o" 
              style={{maxWidth: '100%', border: '1px solid #ddd', borderRadius: '4px', maxHeight: '200px'}}
              onError={(e) => {
                e.target.style.display = 'none';
                document.querySelector('.background-info').innerHTML = 
                  '<small>‚ö†Ô∏è Background n√£o carregado. Certifique-se de que o arquivo background.png est√° na pasta public/img/</small>';
              }}
            />
            <div className="background-info">
              <small>Modelo oficial com cabe√ßalho institucional e assinatura da diretora "Volt√≠lia Maria Costa"</small>
            </div>
          </div>
        </div>

        {/* üî• A√á√ïES PRINCIPAIS - GERAR DECLARA√á√ÉO */}
        <div className="modal-actions">
          <button 
            className="btn-preview"
            onClick={() => gerarPDF(false)}
            disabled={pdfGerado}
          >
            {pdfGerado ? '‚úÖ Declara√ß√£o Gerada' : 'üëÅÔ∏è Gerar Declara√ß√£o'}
          </button>
          
          <button className="btn-cancel" onClick={onClose}>
            ‚ùå Fechar
          </button>
        </div>

        {/* üî• A√á√ïES DE COMPARTILHAMENTO - AP√ìS GERAR PDF */}
        {pdfGerado && (
          <div className="share-section">
            <h4>üì§ Compartilhar Declara√ß√£o:</h4>
            
            <div className="pdf-preview">
              <iframe 
                src={pdfUrl} 
                width="100%" 
                height="400"
                title="Preview da Declara√ß√£o com Background"
                ref={previewRef}
                style={{border: '1px solid #ddd', borderRadius: '8px'}}
              />
            </div>

            <div className="preview-info">
              <p>
                <strong>‚úÖ Declara√ß√£o Gerada com Sucesso!</strong><br/>
                Agora voc√™ pode baixar, imprimir ou compartilhar a declara√ß√£o.
              </p>
            </div>

            <div className="share-actions">
              <button 
                className="btn-download"
                onClick={baixarPDF}
              >
                ‚¨áÔ∏è Baixar PDF
              </button>
              
              <button 
                className="btn-print"
                onClick={imprimirPDF}
              >
                üñ®Ô∏è Imprimir
              </button>
              
              <button 
                className="btn-whatsapp"
                onClick={compartilharWhatsApp}
              >
                üì± WhatsApp
              </button>
              
              <button 
                className="btn-email"
                onClick={compartilharEmail}
              >
                üìß Email
              </button>

              <button 
                className="btn-regenerate"
                onClick={() => {
                  setPdfGerado(false);
                  setPdfUrl('');
                }}
              >
                üîÑ Gerar Outra
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default DeclaracaoModal;
