import React, { useEffect, useState, useCallback, Component } from 'react';
import { fetchStudents, getStudent, updateStudent, deleteStudent, createStudent } from './api';
import DeclaracaoModal from './DeclaracaoModal';
import BoletimModal from './BoletimModal';
import HistoricoModal from './HistoricoModal'; // ðŸ”¥ ADICIONE ESTA IMPORT

// ðŸ”¥ COMPONENTES MOVIDOS PARA FORA DO COMPONENTE App (mantenha os existentes)

// Error Boundary Component (mantenha o existente)
class ErrorBoundary extends Component {
  constructor(props) {
    super(props);
    this.state = { hasError: false };
  }

  static getDerivedStateFromError(error) {
    return { hasError: true };
  }

  componentDidCatch(error, errorInfo) {
    console.log('Erro:', error, errorInfo);
  }

  render() {
    if (this.state.hasError) {
      return <h1>Algo deu errado.</h1>;
    }
    return this.props.children;
  }
}

// ðŸ”¥ FUNÃ‡ÃƒO DE LIMPEZA DE DADOS MELHORADA (mantenha a existente)
const cleanStudentData = (student) => {
  if (!student) return {};
  
  const cleaned = { ...student };
  
  console.log('ðŸ§¹ Dados antes da limpeza:', cleaned);
  
  // ðŸ”¥ REMOVER COMPLETAMENTE TODOS OS CAMPOS PROBLEMÃTICOS
  const camposParaRemover = [
    'Data de Nasc.',
    'Data de Nasc',
    'Data de Masc.',
    'Data de Masc',
    'Data de ',
    '',
    ' ',
    undefined,
    null,
    'undefined',
    'null'
  ];
  
  // ðŸ”¥ REMOVER CAMPOS PROBLEMÃTICOS PRIMEIRO
  Object.keys(cleaned).forEach(key => {
    const shouldRemove = camposParaRemover.some(problema => 
      !key || 
      key.trim() === '' || 
      key.includes(problema) ||
      key.startsWith('Data de')
    );
    
    if (shouldRemove) {
      console.log(`ðŸ—‘ï¸ Removendo campo problemÃ¡tico: "${key}"`);
      delete cleaned[key];
    }
  });
  
  // ðŸ”¥ GARANTIR QUE APENAS OS CAMPOS VÃLIDOS PERMANEÃ‡AM
  const camposValidos = [
    '_id', 'nome', 'dataNascimento', 'cpf', 'cartaoSUS', 'serieAno', 
    'turma', 'turno', 'status', 'transporte', 'localidade', 'nomeMae', 
    'nomePai', 'notas', 'historico', 'createdAt', 'updatedAt' // ðŸ”¥ ADICIONE 'historico'
  ];
  
  Object.keys(cleaned).forEach(key => {
    if (!camposValidos.includes(key)) {
      console.log(`ðŸ—‘ï¸ Removendo campo invÃ¡lido: "${key}"`);
      delete cleaned[key];
    }
  });
  
  // ðŸ”¥ CORREÃ‡ÃƒO EXTRA: Garantir que dataNascimento esteja no formato correto
  if (cleaned.dataNascimento) {
    if (typeof cleaned.dataNascimento === 'string' && cleaned.dataNascimento.includes('/')) {
      // Converter de DD/MM/YYYY para YYYY-MM-DD
      const [day, month, year] = cleaned.dataNascimento.split('/');
      cleaned.dataNascimento = `${year}-${month.padStart(2, '0')}-${day.padStart(2, '0')}`;
    }
  }
  
  // Remover qualquer campo undefined ou null restante
  Object.keys(cleaned).forEach(key => {
    if (cleaned[key] === undefined || cleaned[key] === null || cleaned[key] === '') {
      delete cleaned[key];
    }
  });
  
  console.log('âœ… Dados apÃ³s limpeza completa:', cleaned);
  console.log('ðŸ“¤ Campos que serÃ£o enviados:', Object.keys(cleaned));
  
  return cleaned;
};

// ðŸ”¥ COMPONENTE App PRINCIPAL CORRIGIDO
export default function App() {
  // âœ… CORRETO: Todos os hooks DENTRO do componente
  const [showDeclaracaoModal, setShowDeclaracaoModal] = useState(false);
  const [showBoletimModal, setShowBoletimModal] = useState(false);
  const [showHistoricoModal, setShowHistoricoModal] = useState(false); // ðŸ”¥ NOVO ESTADO
  const [students, setStudents] = useState([]);
  const [allStudents, setAllStudents] = useState([]);
  const [q, setQ] = useState('');
  const [page, setPage] = useState(1);
  const [limit] = useState(50);
  const [loading, setLoading] = useState(false);
  const [selected, setSelected] = useState(null);
  const [editing, setEditing] = useState(false);
  const [creating, setCreating] = useState(false);
  const [total, setTotal] = useState(0);
  const [totalPages, setTotalPages] = useState(0);
  const [seriesStats, setSeriesStats] = useState({});
  const [localidadeStats, setLocalidadeStats] = useState({});
  const [dashboardLoading, setDashboardLoading] = useState(false);
  const [documentType, setDocumentType] = useState('');
  const [showDataPanel, setShowDataPanel] = useState(true);
  const [disciplinas, setDisciplinas] = useState([]);
  
  // Estados para controlar visibilidade dos dashboards
  const [showSeriesDashboard, setShowSeriesDashboard] = useState(false);
  const [showLocalidadeDashboard, setShowLocalidadeDashboard] = useState(false);

  // ðŸ”¥ DEBUG EXTREMO: Verifique TODOS os campos antes do envio
  useEffect(() => {
    if (selected && (editing || creating)) {
      console.log('ðŸ”ðŸ”ðŸ” DEBUG EXTREMO - Todos os campos do aluno:');
      Object.keys(selected).forEach(key => {
        console.log(`- "${key}":`, selected[key]);
      });
    }
  }, [selected, editing, creating]);

  // ðŸ”¥ FUNÃ‡ÃƒO PARA TOGGLE DO PAINEL DE DADOS
  const toggleDataPanel = () => {
    setShowDataPanel(!showDataPanel);
  };

  // ðŸ”¥ FUNÃ‡ÃƒO PARA ABRIR MODAL DE BOLETIM
  const openBoletimModal = () => {
    if (!selected) {
      alert('Selecione um aluno primeiro');
      return;
    }
    // ðŸ”¥ CORREÃ‡ÃƒO: Carregar disciplinas existentes ou inicializar vazio
    if (selected.notas && Array.isArray(selected.notas)) {
      setDisciplinas(selected.notas.map(nota => ({
        ...nota,
        id: nota.id || Date.now().toString() // ðŸ”¥ CORREÃ‡ÃƒO: Garantir ID como string
      })));
    } else {
      setDisciplinas([]);
    }
    setShowBoletimModal(true);
  };

  // ðŸ”¥ CORREÃ‡ÃƒO: Aluno vazio para criaÃ§Ã£o com TODOS os campos
  const emptyStudent = {
    nome: '',
    dataNascimento: '',
    cpf: '',
    cartaoSUS: '',
    serieAno: '',
    turma: '',
    turno: '',
    status: 'Matriculado',
    transporte: '',
    localidade: '',
    nomeMae: '',
    nomePai: ''
  };

  // Carregar TODOS os alunos para o dashboard
  const loadAllStudents = async () => {
    setDashboardLoading(true);
    try {
      const resp = await fetchStudents({ page: 1, limit: 1000, q: '' });
      setAllStudents(resp.data || []);
    } catch (error) {
      console.error('Erro ao carregar dados do dashboard:', error);
    } finally {
      setDashboardLoading(false);
    }
  };

  // Carregar alunos com busca inteligente
  async function load(searchTerm = q) {
    setLoading(true);
    try {
      const resp = await fetchStudents({ page, limit, q: searchTerm });
      setStudents(resp.data || []);
      setTotal(resp.total || 0);
      setTotalPages(resp.totalPages || 0);
    } catch (error) {
      console.error('Erro ao carregar alunos:', error);
      alert('Erro ao carregar dados');
    } finally {
      setLoading(false);
    }
  }

  // Calcular estatÃ­sticas com TODOS os alunos
  useEffect(() => {
    if (allStudents.length > 0) {
      const statsSeries = {};
      const statsLocalidades = {};
      let totalGeral = 0;
      
      allStudents.forEach(student => {
        const serie = student.serieAno?.toString().trim() || 'NÃ£o informada';
        const turma = student.turma?.toString().trim() || 'Sem turma';
        const localidade = student.localidade?.toString().trim() || 'NÃ£o informada';
        
        totalGeral++;
        
        // EstatÃ­stica por sÃ©rie
        if (!statsSeries[serie]) {
          statsSeries[serie] = {
            count: 0,
            turmas: {}
          };
        }
        statsSeries[serie].count++;
        
        if (!statsSeries[serie].turmas[turma]) {
          statsSeries[serie].turmas[turma] = 0;
        }
        statsSeries[serie].turmas[turma]++;
        
        // EstatÃ­stica por localidade
        if (!statsLocalidades[localidade]) {
          statsLocalidades[localidade] = {
            count: 0,
            series: {}
          };
        }
        statsLocalidades[localidade].count++;
        
        // EstatÃ­stica por sÃ©rie dentro da localidade
        if (!statsLocalidades[localidade].series[serie]) {
          statsLocalidades[localidade].series[serie] = 0;
        }
        statsLocalidades[localidade].series[serie]++;
      });
      
      statsSeries.totalGeral = totalGeral;
      setSeriesStats(statsSeries);
      setLocalidadeStats(statsLocalidades);
    } else {
      setSeriesStats({});
      setLocalidadeStats({});
    }
  }, [allStudents]);

  // Carregar dados do dashboard na inicializaÃ§Ã£o
  useEffect(() => {
    loadAllStudents();
    load();
  }, []);

  // Debounce para pesquisa
  const debouncedSearch = useCallback((searchTerm) => {
    const timer = setTimeout(() => {
      setPage(1);
      load(searchTerm);
    }, 500);
    return () => clearTimeout(timer);
  }, []);

  useEffect(() => {
    if (q !== '') {
      debouncedSearch(q);
    } else {
      setPage(1);
      load();
    }
  }, [q, debouncedSearch]);

  // Recarregar quando mudar pÃ¡gina
  useEffect(() => { 
    if (q === '') {
      load();
    }
  }, [page]);

  // Recarregar dashboard apÃ³s criar/editar/excluir
  const handleStudentChange = () => {
    loadAllStudents();
    load();
  };

  // Busca manual
  function handleSearch() {
    setPage(1);
    load();
  }

  // Abrir detalhes do aluno
  async function openDetail(id) {
    try {
      console.log('ðŸ” Buscando aluno ID:', id);
      const s = await getStudent(id);
      console.log('âœ… Aluno carregado:', s);
      
      // ðŸ”¥ CORREÃ‡ÃƒO: Limpar dados ao carregar tambÃ©m
      const cleanedStudent = cleanStudentData(s);
      setSelected(cleanedStudent);
      setEditing(false);
      setCreating(false);
      setDocumentType(''); // Resetar tipo de documento
    } catch (error) {
      console.error('âŒ Erro ao carregar detalhes:', error);
      alert('Erro ao carregar detalhes do aluno: ' + error.message);
    }
  }

  // ðŸ”¥ FUNÃ‡ÃƒO DE SALVAR COMPLETAMENTE CORRIGIDA
  async function save() {
    if (!selected || !selected._id) {
      alert('Nenhum aluno selecionado para salvar');
      return;
    }
    try {
      console.log('ðŸ’¾ Salvando aluno (ORIGINAL):', selected);
      
      // ðŸ”¥ CORREÃ‡ÃƒO: Limpar DRASTICAMENTE antes do envio
      const cleanedData = cleanStudentData(selected);
      
      console.log('ðŸ§¹ Dados limpos para envio:', cleanedData);
      console.log('ðŸ” Verificando se hÃ¡ campos problemÃ¡ticos...');
      
      // ðŸ”¥ VERIFICAÃ‡ÃƒO FINAL - garantir que nÃ£o hÃ¡ campos problemÃ¡ticos
      const camposProibidos = ['Data de Nasc.', 'Data de Nasc', 'Data de Masc.', 'Data de Masc'];
      const temCampoProibido = Object.keys(cleanedData).some(key => 
        camposProibidos.some(proibido => key.includes(proibido))
      );
      
      if (temCampoProibido) {
        alert('âŒ AINDA EXISTEM CAMPOS PROBLEMÃTICOS! NÃ£o serÃ¡ enviado.');
        return;
      }
      
      const res = await updateStudent(selected._id, cleanedData);
      
      // ðŸ”¥ CORREÃ‡ÃƒO: Limpar tambÃ©m a resposta
      const cleanedResponse = cleanStudentData(res);
      setSelected(cleanedResponse);
      setEditing(false);
      handleStudentChange();
      alert('Aluno atualizado com sucesso!');
    } catch (error) {
      console.error('âŒ Erro ao salvar:', error);
      
      // ðŸ”¥ DEBUG COMPLETO DO ERRO
      console.log('ðŸ” DEBUG COMPLETO DO ERRO:');
      console.log('- selected:', selected);
      console.log('- Object.keys(selected):', Object.keys(selected));
      
      alert('Erro ao salvar alteraÃ§Ãµes: ' + error.message);
    }
  }

  // Excluir aluno
  async function remove(id) {
    if (!id) {
      alert('ID do aluno nÃ£o encontrado');
      return;
    }
    
    if (!window.confirm('Tem certeza que deseja excluir este aluno?')) return;
    
    try {
      console.log('ðŸ—‘ï¸ Excluindo aluno ID:', id);
      await deleteStudent(id);
      setSelected(null);
      handleStudentChange();
      alert('Aluno excluÃ­do com sucesso!');
    } catch (error) {
      console.error('âŒ Erro ao excluir:', error);
      alert('Erro ao excluir aluno: ' + error.message);
    }
  }

  // Criar novo aluno
  async function createNewStudent() {
    if (!selected) {
      alert('Preencha os dados do aluno');
      return;
    }

    // ValidaÃ§Ã£o bÃ¡sica no frontend
    if (!selected.nome || !selected.nome.trim()) {
      alert('Nome Ã© obrigatÃ³rio');
      return;
    }

    try {
      console.log('âž• Criando novo aluno:', selected);
      
      // ðŸ”¥ CORREÃ‡ÃƒO: Limpar dados antes do envio
      const cleanedData = cleanStudentData(selected);
      console.log('ðŸ§¹ Dados limpos para criaÃ§Ã£o:', cleanedData);

      const createdStudent = await createStudent(cleanedData);
      
      alert('Aluno criado com sucesso!');
      setCreating(false);
      setSelected(null);
      handleStudentChange();
      
    } catch (error) {
      console.error('âŒ Erro ao criar aluno:', error);
      
      // Mensagem de erro mais amigÃ¡vel
      let errorMessage = 'Erro ao criar aluno';
      
      if (error.message.includes('400')) {
        errorMessage = 'Dados invÃ¡lidos. Verifique os campos obrigatÃ³rios.';
      } else if (error.message.includes('CPF jÃ¡ cadastrado')) {
        errorMessage = 'CPF jÃ¡ estÃ¡ cadastrado no sistema.';
      } else if (error.message.includes('NetworkError')) {
        errorMessage = 'Erro de conexÃ£o. Verifique se o servidor estÃ¡ rodando.';
      } else {
        errorMessage = error.message || 'Erro desconhecido ao criar aluno';
      }
      
      alert(errorMessage);
    }
  }

  // Iniciar criaÃ§Ã£o
  function startCreate() {
    setSelected({...emptyStudent});
    setCreating(true);
    setEditing(false);
    setDocumentType('');
  }

  // Cancelar criaÃ§Ã£o
  function cancelCreate() {
    setCreating(false);
    setSelected(null);
    setDocumentType('');
  }

  // ðŸ”¥ CORREÃ‡ÃƒO COMPLETA: FunÃ§Ã£o para salvar notas
  async function salvarNotas() {
    if (!selected) return;
    
    try {
      // ðŸ”¥ CORREÃ‡ÃƒO: Garantir que todas as disciplinas tenham IDs vÃ¡lidos e formato correto
      const disciplinasFormatadas = disciplinas.map(disciplina => ({
        id: disciplina.id || `disciplina_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
        nome: disciplina.nome || 'Disciplina nÃ£o nomeada',
        serie: disciplina.serie || selected?.serieAno || '',
        // ðŸ”¥ CORREÃ‡ÃƒO: Garantir que todos os campos numÃ©ricos sejam numbers
        bimestre1: parseFloat(disciplina.bimestre1) || 0,
        bimestre2: parseFloat(disciplina.bimestre2) || 0,
        bimestre3: parseFloat(disciplina.bimestre3) || 0,
        bimestre4: parseFloat(disciplina.bimestre4) || 0,
        mediaFinal: parseFloat(disciplina.mediaFinal) || 0,
        situacao: disciplina.situacao || 'Em RecuperaÃ§Ã£o'
      }));

      const alunoAtualizado = {
        ...selected,
        notas: disciplinasFormatadas
      };
      
      // ðŸ”¥ CORREÃ‡ÃƒO: Limpar dados antes do envio
      const cleanedData = cleanStudentData(alunoAtualizado);
      console.log('ðŸ“Š Salvando notas:', cleanedData);
      
      await updateStudent(selected._id, cleanedData);
      
      // ðŸ”¥ CORREÃ‡ÃƒO: Limpar tambÃ©m a resposta
      const cleanedResponse = cleanStudentData(alunoAtualizado);
      setSelected(cleanedResponse);
      setShowBoletimModal(false);
      alert('Notas salvas com sucesso!');
      
      // Recarregar dados
      handleStudentChange();
    } catch (error) {
      console.error('âŒ Erro ao salvar notas:', error);
      alert('Erro ao salvar notas: ' + error.message);
    }
  }

  // Pesquisas rÃ¡pidas por sÃ©rie/turma
  function quickSearch(term) {
    setQ(term);
    setPage(1);
  }

  return (
    <ErrorBoundary>
      <div className="container">
        <header>
          <div>
            <h1>GEA (Gerenciamento Educacional e Administrativo)</h1>
            <h2>E. E. F. NAIR CUNHA DE AGUIAR</h2>
          </div>
          <div className="controls">
            <input 
              placeholder="Buscar por: nome, CPF, sÃ©rie, turma, localidade..." 
              value={q} 
              onChange={e => setQ(e.target.value)}
              onKeyPress={e => e.key === 'Enter' && handleSearch()}
            />
            <button onClick={handleSearch}>Buscar</button>
            
            <button 
              onClick={startCreate}
              style={{ background: '#38a169', marginLeft: '8px' }}
            >
              + Novo Aluno
            </button>
            
            {/* ðŸ”¥ BOTÃƒO PARA OCULTAR/MOSTRAR DADOS */}
            <button 
              onClick={toggleDataPanel}
              style={{ 
                background: showDataPanel ? '#ed8936' : '#4299e1', 
                marginLeft: '8px' 
              }}
            >
              {showDataPanel ? 'ðŸ“‹ Ocultar Dados' : 'ðŸ“‹ Mostrar Dados'}
            </button>
            
            {/* BotÃµes para mostrar/ocultar dashboards */}
            <button 
              onClick={() => setShowSeriesDashboard(!showSeriesDashboard)}
              style={{ 
                background: showSeriesDashboard ? '#ed8936' : '#4299e1', 
                marginLeft: '8px' 
              }}
            >
              {showSeriesDashboard ? 'ðŸ“Š Ocultar SÃ©ries' : 'ðŸ“Š Ver SÃ©ries'}
            </button>
            
            <button 
              onClick={() => setShowLocalidadeDashboard(!showLocalidadeDashboard)}
              style={{ 
                background: showLocalidadeDashboard ? '#ed8936' : '#48bb78', 
                marginLeft: '8px' 
              }}
            >
              {showLocalidadeDashboard ? 'ðŸ˜ï¸ Ocultar Localidades' : 'ðŸ˜ï¸ Ver Localidades'}
            </button>
            
            {q && (
              <button 
                onClick={() => {
                  setQ('');
                  setPage(1);
                }}
                style={{ marginLeft: '8px', background: '#718096' }}
              >
                Limpar
              </button>
            )}
          </div>
          
          {/* Pesquisas rÃ¡pidas */}
          <div className="quick-search">
            <small>
              ðŸ” <strong>Pesquisas rÃ¡pidas:</strong>
              <button onClick={() => quickSearch('8 A')} className="quick-btn">8Â° A</button>
              <button onClick={() => quickSearch('8 B')} className="quick-btn">8Â° B</button>
              <button onClick={() => quickSearch('8')} className="quick-btn">8Â° Ano</button>
              <button onClick={() => quickSearch('9 U')} className="quick-btn">9Â° U</button>
              <button onClick={() => quickSearch('2 U')} className="quick-btn">2Â° U</button>
            </small>
          </div>

          {!q && (
            <div className="search-tips">
              <small>
                ðŸ’¡ <strong>Dicas:</strong> 
                "2" (sÃ©rie) â€¢ "A" (turma) â€¢ "09565384366" (CPF) â€¢ "Goiabeira" (localidade) â€¢ "Ana" (nome) â€¢ "8 A" (sÃ©rie e turma)
              </small>
            </div>
          )}
        </header>

        {/* DASHBOARDS CONDICIONAIS - SÃ“ MOSTRAR SE O BOTÃƒO FOR CLICADO */}
        {showSeriesDashboard && (
          <StatsDashboard 
            seriesStats={seriesStats}
            allStudents={allStudents}
            dashboardLoading={dashboardLoading}
          />
        )}

        {showLocalidadeDashboard && (
          <LocalidadeDashboard 
            localidadeStats={localidadeStats}
            seriesStats={seriesStats}
            allStudents={allStudents}
            dashboardLoading={dashboardLoading}
          />
        )}

        {/* PAINEL DE DETALHES COMPLETO E CORRIGIDO */}
        {showDataPanel && (
          <section className="detail-panel">
            {selected ? (
              <div className="detail-content">
                <h2>
                  {creating ? 'Novo Aluno' : editing ? 'Editando Aluno' : 'Detalhes do Aluno'}
                </h2>
                
                {editing || creating ? (
                  <div className="form">
                    <label>
                      Nome:
                      <input 
                        value={selected.nome || ''} 
                        onChange={e => setSelected({...selected, nome: e.target.value})}
                        placeholder="Nome completo do aluno"
                      />
                    </label>
                    
                    {/* ðŸ”¥ CORREÃ‡ÃƒO: Campo com nome CORRETO e consistente */}
                    <label>
                      Data de Nascimento:
                      <input 
                        type="date"
                        name="dataNascimento" // ðŸ”¥ ADICIONE ESTE name
                        value={selected.dataNascimento ? new Date(selected.dataNascimento).toISOString().split('T')[0] : ''} 
                        onChange={e => setSelected({...selected, dataNascimento: e.target.value})}
                      />
                    </label>
                    
                    <label>
                      CPF:
                      <input 
                        value={selected.cpf || ''} 
                        onChange={e => setSelected({...selected, cpf: e.target.value})}
                        placeholder="000.000.000-00"
                      />
                    </label>
                    
                    {/* ðŸ”¥ CORREÃ‡ÃƒO: Campos com placeholders e valores padrÃ£o */}
                    <label>
                      Nome da MÃ£e:
                      <input 
                        value={selected.nomeMae || ''} 
                        onChange={e => setSelected({...selected, nomeMae: e.target.value})}
                        placeholder="Nome completo da mÃ£e"
                      />
                    </label>
                    <label>
                      Nome do Pai:
                      <input 
                        value={selected.nomePai || ''} 
                        onChange={e => setSelected({...selected, nomePai: e.target.value})}
                        placeholder="Nome completo do pai"
                      />
                    </label>
                    <label>
                      Status:
                      <select 
                        value={selected.status || 'Matriculado'} 
                        onChange={e => setSelected({...selected, status: e.target.value})}
                      >
                        <option value="Matriculado">Matriculado</option>
                        <option value="Transferido">Transferido</option>
                        <option value="ConcluÃ­do">ConcluÃ­do</option>
                      </select>
                    </label>
                    
                    <label>
                      SÃ©rie/Ano:
                      <input 
                        value={selected.serieAno || ''} 
                        onChange={e => setSelected({...selected, serieAno: e.target.value})}
                        placeholder="Ex: 8"
                      />
                    </label>
                    <label>
                      Turma:
                      <input 
                        value={selected.turma || ''} 
                        onChange={e => setSelected({...selected, turma: e.target.value})}
                        placeholder="Ex: A"
                      />
                    </label>
                    <label>
                      Localidade:
                      <input 
                        value={selected.localidade || ''} 
                        onChange={e => setSelected({...selected, localidade: e.target.value})}
                        placeholder="Ex: Goiabeira"
                      />
                    </label>
                    
                    <div className="actions">
                      {creating ? (
                        <>
                          <button onClick={createNewStudent}>Criar Aluno</button>
                          <button onClick={cancelCreate}>Cancelar</button>
                        </>
                      ) : (
                        <>
                          <button onClick={save}>Salvar</button>
                          <button onClick={() => setEditing(false)}>Cancelar</button>
                        </>
                      )}
                    </div>
                  </div>
                ) : (
                  <div className="read">
                    <div><strong>Nome:</strong> {selected.nome}</div>
                    <div><strong>Data Nasc.:</strong> {selected.dataNascimento ? new Date(selected.dataNascimento).toLocaleDateString('pt-BR') : ''}</div>
                    <div><strong>CPF:</strong> {selected.cpf}</div>
                    
                    {/* ðŸ”¥ NOVOS CAMPOS EXIBIÃ‡ÃƒO */}
                    <div><strong>Nome da MÃ£e:</strong> {selected.nomeMae || 'NÃ£o informado'}</div>
                    <div><strong>Nome do Pai:</strong> {selected.nomePai || 'NÃ£o informado'}</div>
                    <div><strong>Status:</strong> {selected.status || 'Matriculado'}</div>
                    
                    <div><strong>SÃ©rie/Ano:</strong> {selected.serieAno}</div>
                    <div><strong>Turma:</strong> {selected.turma}</div>
                    <div><strong>Localidade:</strong> {selected.localidade}</div>
                    
                    {/* BotÃµes de documentos */}
                    <div className="document-actions" style={{ margin: '15px 0', padding: '10px', background: '#f7fafc', borderRadius: '6px' }}>
                      <h4>ðŸ“„ Gerar Documentos:</h4>
                      <div style={{ display: 'flex', gap: '8px', flexWrap: 'wrap' }}>
                        <button 
                          onClick={() => setShowDeclaracaoModal(true)}
                          style={{ background: '#4299e1' }}
                        >
                          ðŸ“ DeclaraÃ§Ã£o
                        </button>

                        <button 
                          onClick={openBoletimModal}
                          style={{ background: '#48bb78' }}
                        >
                          ðŸ“Š Boletim
                        </button>
                        
                        {/* ðŸ”¥ NOVO BOTÃƒO DE HISTÃ“RICO */}
                        <button 
                          onClick={() => setShowHistoricoModal(true)}
                          style={{ background: '#ed8936' }}
                        >
                          ðŸ“š HistÃ³rico
                        </button>
                      </div>
                    </div>
                    
                    <div className="actions">
                      <button onClick={() => setEditing(true)}>âœï¸ Editar</button>
                      <button onClick={() => remove(selected._id)} style={{background: '#e53e3e'}}>
                        ðŸ—‘ï¸ Excluir
                      </button>
                      <button onClick={() => setSelected(null)}>âŒ Fechar</button>
                    </div>
                  </div>
                )}
              </div>
            ) : (
              <div className="empty-detail">
                {creating ? 'Preencha os dados do novo aluno' : 'Selecione um aluno para ver os detalhes'}
              </div>
            )}
          </section>
        )}

        <main>
          <section className="list">
            <div className="meta">
              <div>Total na busca: {total}</div>
              {q && <div>Filtro: "{q}"</div>}
              <div>
                PÃ¡gina {page} de {totalPages} â€” 
                <button 
                  disabled={page <= 1}
                  onClick={() => setPage(p => Math.max(1, p - 1))}
                >â—€</button>
                <button 
                  disabled={page >= totalPages}
                  onClick={() => setPage(p => p + 1)}
                >â–¶</button>
              </div>
            </div>

            {loading ? (
              <div>Carregando...</div>
            ) : students.length === 0 ? (
              <div style={{ padding: '20px', textAlign: 'center', color: '#718096' }}>
                {q ? `Nenhum aluno encontrado para "${q}"` : 'Nenhum aluno cadastrado'}
              </div>
            ) : (
              <table>
                <thead>
                  <tr>
                    <th>Nome</th>
                    <th>Data Nasc.</th>
                    <th>CPF</th>
                    <th>SÃ©rie</th>
                    <th>Turma</th>
                    <th>Localidade</th>
                    <th></th>
                  </tr>
                </thead>
                <tbody>
                  {students.map(s => (
                    <tr key={s._id || s.cpf}>
                      <td>{s.nome}</td>
                      <td>{s.dataNascimento ? new Date(s.dataNascimento).toLocaleDateString('pt-BR') : ''}</td>
                      <td>{s.cpf}</td>
                      <td>{s.serieAno}</td>
                      <td>{s.turma}</td>
                      <td>{s.localidade}</td>
                      <td>
                        <button onClick={() => openDetail(s._id)}>Abrir</button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            )}
          </section>
        </main>

        {/* âœ… CORRETO: Modal FORA da tabela, no final do componente */}
        {showDeclaracaoModal && (
          <DeclaracaoModal 
            aluno={selected}
            onClose={() => setShowDeclaracaoModal(false)}
          />
        )}

        {showBoletimModal && (
          <BoletimModal 
            aluno={selected}
            disciplinas={disciplinas}
            setDisciplinas={setDisciplinas}
            onClose={() => setShowBoletimModal(false)}
            onSave={salvarNotas}
          />
        )}

        {/* ðŸ”¥ NOVO MODAL DE HISTÃ“RICO */}
        {showHistoricoModal && (
          <HistoricoModal 
            aluno={selected}
            onClose={() => setShowHistoricoModal(false)}
          />
        )}

        <footer>
          <small>API: http://localhost:4000/api/students</small>
        </footer>
      </div>
    </ErrorBoundary>
  );
}
