import React, { useEffect, useState, useCallback } from 'react';
import { fetchStudents, getStudent, updateStudent, deleteStudent, createStudent } from './api';

export default function App() {
  const [students, setStudents] = useState([]);
  const [q, setQ] = useState('');
  const [page, setPage] = useState(1);
  const [limit] = useState(50);
  const [loading, setLoading] = useState(false);
  const [selected, setSelected] = useState(null);
  const [editing, setEditing] = useState(false);
  const [creating, setCreating] = useState(false); // üî• NOVO ESTADO
  const [total, setTotal] = useState(0);
  const [totalPages, setTotalPages] = useState(0);

  // üî• NOVO ALUNO EM BRANCO
  const emptyStudent = {
    nome: '',
    dataNascimento: '',
    cpf: '',
    cartaoSUS: '',
    serieAno: '',
    turma: '',
    turno: '',
    status: '',
    transporte: '',
    localidade: ''
  };

  // ... (mantenha todas as fun√ß√µes anteriores: debouncedSearch, load, handleSearch, openDetail, save, remove)

  // üî• FUN√á√ÉO PARA CRIAR NOVO ALUNO
  async function createNewStudent() {
    try {
      const newStudent = await createStudent(selected);
      alert('Aluno criado com sucesso!');
      setCreating(false);
      setSelected(null);
      load(); // Recarrega a lista
    } catch (error) {
      console.error('Erro ao criar aluno:', error);
      alert('Erro ao criar aluno');
    }
  }

  // üî• FUN√á√ÉO PARA INICIAR CRIA√á√ÉO
  function startCreate() {
    setSelected(emptyStudent);
    setCreating(true);
    setEditing(false);
  }

  // üî• FUN√á√ÉO PARA CANCELAR CRIA√á√ÉO
  function cancelCreate() {
    setCreating(false);
    setSelected(null);
  }

  return (
    <div className="container">
      <header>
        <h1>Alunos NCA</h1>
        <div className="controls">
          <input 
            placeholder="Pesquisar por nome, CPF, turma, s√©rie ou localidade..." 
            value={q} 
            onChange={e => setQ(e.target.value)}
            onKeyPress={e => e.key === 'Enter' && handleSearch()}
          />
          <button onClick={handleSearch}>Buscar</button>
          
          {/* üî• BOT√ÉO NOVO ALUNO */}
          <button 
            onClick={startCreate}
            style={{ 
              background: '#38a169', 
              marginLeft: '8px' 
            }}
          >
            + Novo Aluno
          </button>
          
          {q && (
            <button 
              onClick={() => {
                setQ('');
                setPage(1);
              }}
              style={{ marginLeft: '8px', background: '#718096' }}
            >
              Limpar
            </button>
          )}
        </div>
      </header>

      <main>
        <section className="list">
          <div className="meta">
            <div>Total: {total}</div>
            {q && <div>Filtro: "{q}"</div>}
            <div>
              P√°gina {page} de {totalPages} ‚Äî 
              <button 
                disabled={page <= 1}
                onClick={() => setPage(p => Math.max(1, p - 1))}
              >‚óÄ</button>
              <button 
                disabled={page >= totalPages}
                onClick={() => setPage(p => p + 1)}
              >‚ñ∂</button>
            </div>
          </div>

          {loading ? <div>Carregando...</div> :
            students.length === 0 ? (
              <div style={{ padding: '20px', textAlign: 'center', color: '#718096' }}>
                {q ? `Nenhum aluno encontrado para "${q}"` : 'Nenhum aluno cadastrado'}
              </div>
            ) : (
              <table>
                <thead>
                  <tr>
                    <th>Nome</th><th>Data Nasc.</th><th>CPF</th><th>S√©rie</th><th>Turma</th><th>Localidade</th><th></th>
                  </tr>
                </thead>
                <tbody>
                  {students.map(s => (
                    <tr key={s._id || s.cpf}>
                      <td>{s.nome}</td>
                      <td>{s.dataNascimento ? new Date(s.dataNascimento).toLocaleDateString('pt-BR') : ''}</td>
                      <td>{s.cpf}</td>
                      <td>{s.serieAno}</td>
                      <td>{s.turma}</td>
                      <td>{s.localidade}</td>
                      <td><button onClick={() => openDetail(s._id)}>Abrir</button></td>
                    </tr>
                  ))}
                </tbody>
              </table>
            )
          }
        </section>

        <aside className="detail">
          {selected ? (
            <div>
              <h2>
                {creating ? 'Novo Aluno' : 'Detalhes do Aluno'}
              </h2>
              
              {creating ? (
                // üî• FORMUL√ÅRIO DE CRIA√á√ÉO
                <div className="form">
                  <label>
                    Nome *
                    <input 
                      value={selected.nome || ''} 
                      onChange={e => setSelected({...selected, nome: e.target.value})}
                      placeholder="Digite o nome completo"
                    />
                  </label>
                  
                  <label>
                    Data Nasc.
                    <input 
                      type="date"
                      value={selected.dataNascimento ? new Date(selected.dataNascimento).toISOString().split('T')[0] : ''}
                      onChange={e => setSelected({...selected, dataNascimento: e.target.value})}
                    />
                  </label>
                  
                  <label>
                    CPF
                    <input 
                      value={selected.cpf || ''} 
                      onChange={e => setSelected({...selected, cpf: e.target.value})}
                      placeholder="000.000.000-00"
                    />
                  </label>
                  
                  <label>
                    Cart√£o SUS
                    <input 
                      value={selected.cartaoSUS || ''} 
                      onChange={e => setSelected({...selected, cartaoSUS: e.target.value})}
                      placeholder="N√∫mero do cart√£o SUS"
                    />
                  </label>
                  
                  <label>
                    S√©rie/Ano
                    <input 
                      value={selected.serieAno || ''} 
                      onChange={e => setSelected({...selected, serieAno: e.target.value})}
                      placeholder="Ex: 8, 1, 4"
                    />
                  </label>
                  
                  <label>
                    Turma
                    <input 
                      value={selected.turma || ''} 
                      onChange={e => setSelected({...selected, turma: e.target.value})}
                      placeholder="Ex: A, B, U"
                    />
                  </label>
                  
                  <label>
                    Localidade
                    <input 
                      value={selected.localidade || ''} 
                      onChange={e => setSelected({...selected, localidade: e.target.value})}
                      placeholder="Ex: Goiabeira, Oiticica"
                    />
                  </label>
                  
                  <div className="actions">
                    <button 
                      onClick={createNewStudent}
                      disabled={!selected.nome.trim()} // Nome obrigat√≥rio
                      style={{ background: '#38a169' }}
                    >
                      Criar Aluno
                    </button>
                    <button onClick={cancelCreate}>Cancelar</button>
                  </div>
                </div>
              ) : !editing ? (
                // MODO LEITURA (visualizar)
                <div className="read">
                  <div><strong>Nome:</strong> {selected.nome}</div>
                  <div><strong>CPF:</strong> {selected.cpf}</div>
                  <div><strong>Cart√£o SUS:</strong> {selected.cartaoSUS}</div>
                  <div><strong>Data Nasc.:</strong> {selected.dataNascimento ? new Date(selected.dataNascimento).toLocaleDateString('pt-BR') : ''}</div>
                  <div><strong>S√©rie/Ano:</strong> {selected.serieAno}</div>
                  <div><strong>Turma:</strong> {selected.turma}</div>
                  <div><strong>Localidade:</strong> {selected.localidade}</div>
                  <div className="actions">
                    <button onClick={() => setEditing(true)}>Editar</button>
                    <button onClick={() => remove(selected._id)}>Excluir</button>
                    <button onClick={() => setSelected(null)}>Fechar</button>
                  </div>
                </div>
              ) : (
                // MODO EDI√á√ÉO
                <div className="form">
                  <label>Nome<input value={selected.nome || ''} onChange={e => setSelected({...selected, nome: e.target.value})} /></label>
                  <label>CPF<input value={selected.cpf || ''} onChange={e => setSelected({...selected, cpf: e.target.value})} /></label>
                  <label>Localidade<input value={selected.localidade || ''} onChange={e => setSelected({...selected, localidade: e.target.value})} /></label>
                  <div className="actions">
                    <button onClick={save}>Salvar</button>
                    <button onClick={() => setEditing(false)}>Cancelar</button>
                  </div>
                </div>
              )}
            </div>
          ) : (
            <div>
              <p>Selecione um aluno para ver detalhes</p>
              <button 
                onClick={startCreate}
                style={{ 
                  background: '#38a169', 
                  width: '100%',
                  marginTop: '10px'
                }}
              >
                + Adicionar Novo Aluno
              </button>
            </div>
          )}
        </aside>
      </main>

      <footer>
        <small>API: http://localhost:4000/api/students</small>
      </footer>
    </div>
  );
}
