import React, { useState } from 'react';
import jsPDF from 'jspdf';
import './DeclaracaoModal.css';

const DeclaracaoModal = ({ aluno, onClose, onGerarDeclaracao }) => {
  const [tipoDeclaracao, setTipoDeclaracao] = useState('matricula');
  const [mostrarPreview, setMostrarPreview] = useState(false);
  const [pdfUrl, setPdfUrl] = useState('');

  const tiposDeclaracao = {
    matricula: {
      nome: 'Declara√ß√£o de Matr√≠cula',
      template: (aluno) => {
        const dataNasc = aluno.dataNascimento ? new Date(aluno.dataNascimento).toLocaleDateString('pt-BR') : '___/___/_____';
        const serie = aluno.serieAno || '____';
        const turma = aluno.turma ? ` ${aluno.turma}` : '';
        const nivel = parseInt(serie) <= 5 ? 'I' : 'II';
        
        return `Declaramos para os devidos fins que ${aluno.nome}, nascido(a) em ${dataNasc}, est√° regularmente matriculado(a) no ${serie}¬∫ ano${turma} do Ensino Fundamental ${nivel} desta institui√ß√£o de ensino.

Documento v√°lido para comprova√ß√£o de matr√≠cula junto aos √≥rg√£os competentes.`;
      }
    },
    transferencia: {
      nome: 'Declara√ß√£o de Transfer√™ncia',
      template: (aluno) => {
        const dataNasc = aluno.dataNascimento ? new Date(aluno.dataNascimento).toLocaleDateString('pt-BR') : '___/___/_____';
        const serie = aluno.serieAno || '____';
        const turma = aluno.turma ? ` ${aluno.turma}` : '';
        
        return `Declaramos que ${aluno.nome}, nascido(a) em ${dataNasc}, est√° regularmente matriculado(a) no ${serie}¬∫ ano${turma} e solicita transfer√™ncia para outra institui√ß√£o de ensino.

Esta declara√ß√£o atesta a regularidade de sua situa√ß√£o escolar.`;
      }
    },
    frequencia: {
      nome: 'Declara√ß√£o de Frequ√™ncia',
      template: (aluno) => {
        const serie = aluno.serieAno || '____';
        const turma = aluno.turma ? ` ${aluno.turma}` : '';
        const turno = aluno.turno || '_______';
        
        return `Certificamos que ${aluno.nome} frequenta regularmente as aulas no ${serie}¬∫ ano${turma} no turno ${turno} desta institui√ß√£o.

Atestamos que o(a) aluno(a) encontra-se em dia com suas obriga√ß√µes escolares.`;
      }
    },
    vaga: {
      nome: 'Declara√ß√£o de Vaga',
      template: (aluno) => {
        const serie = aluno.serieAno || '____';
        const turma = aluno.turma ? ` ${aluno.turma}` : '';
        
        return `Declaramos que temos vaga dispon√≠vel no ${serie}¬∫ ano${turma} para o(a) aluno(a) ${aluno.nome}.

Confirmamos a disponibilidade de vaga na s√©rie/turma solicitada.`;
      }
    }
  };

  const gerarPDF = (download = false) => {
    const doc = new jsPDF("portrait", "pt", "a4");
    const margem = 40;
    const largura = doc.internal.pageSize.getWidth();
    const altura = doc.internal.pageSize.getHeight();
    
    // Cabe√ßalho
    doc.setFont("Times", "Bold");
    doc.setFontSize(14);
    doc.text("E. E. F. NAIR CUNHA DE AGUIAR", largura / 2, 60, { align: "center" });
    
    doc.setFont("Times", "Normal");
    doc.setFontSize(12);
    doc.text("S√≠tio Oiticica ‚Äì Frecheirinha ‚Äì CE", largura / 2, 80, { align: "center" });
    doc.text("Email: eefnaircunhadeaguiar@gmail.com", largura / 2, 100, { align: "center" });

    // T√≠tulo
    doc.setFont("Times", "Bold");
    doc.setFontSize(18);
    doc.text("DECLARA√á√ÉO", largura / 2, 150, { align: "center" });

    // Texto da declara√ß√£o
    doc.setFont("Times", "Normal");
    doc.setFontSize(12);
    
    const template = tiposDeclaracao[tipoDeclaracao].template(aluno);
    const linhas = doc.splitTextToSize(template, largura - margem * 2);
    doc.text(linhas, margem, 200);

    // Data e local
    const hoje = new Date().toLocaleDateString('pt-BR');
    doc.text(`S√≠tio Oiticica, Frecheirinha ‚Äì CE, ${hoje}.`, margem, 350);

    // Assinatura
    doc.setFont("Times", "Bold");
    doc.text("_________________________________", margem, 420);
    doc.text("Maria do Socorro Oliveira de Aguiar", margem, 440);
    doc.text("Diretora Escolar", margem, 460);

    if (download) {
      doc.save(`Declaracao_${aluno.nome}_${tipoDeclaracao}.pdf`);
      alert('Declara√ß√£o baixada com sucesso!');
    } else {
      // Criar URL para preview
      const pdfBlob = doc.output('blob');
      const url = URL.createObjectURL(pdfBlob);
      setPdfUrl(url);
      setMostrarPreview(true);
    }
  };

  const compartilharWhatsApp = () => {
    const texto = `Declara√ß√£o de ${aluno.nome} - ${tiposDeclaracao[tipoDeclaracao].nome}`;
    const url = `https://wa.me/?text=${encodeURIComponent(texto)}`;
    window.open(url, '_blank');
  };

  const compartilharEmail = () => {
    const assunto = `Declara√ß√£o - ${aluno.nome}`;
    const corpo = `Segue em anexo a declara√ß√£o de ${tiposDeclaracao[tipoDeclaracao].nome} para ${aluno.nome}.`;
    const mailto = `mailto:?subject=${encodeURIComponent(assunto)}&body=${encodeURIComponent(corpo)}`;
    window.location.href = mailto;
  };

  if (!aluno) return null;

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        {!mostrarPreview ? (
          <>
            <h2>üìÑ Gerar Declara√ß√£o</h2>
            
            <div className="aluno-info">
              <h3>Aluno: {aluno.nome}</h3>
              <p>S√©rie: {aluno.serieAno}¬∫ ano {aluno.turma}</p>
              {aluno.dataNascimento && (
                <p>Nascimento: {new Date(aluno.dataNascimento).toLocaleDateString('pt-BR')}</p>
              )}
            </div>

            <div className="tipo-selecao">
              <label>Tipo de Declara√ß√£o:</label>
              <select 
                value={tipoDeclaracao} 
                onChange={(e) => setTipoDeclaracao(e.target.value)}
              >
                <option value="matricula">üìù Matr√≠cula</option>
                <option value="transferencia">üîÑ Transfer√™ncia</option>
                <option value="frequencia">üìä Frequ√™ncia</option>
                <option value="vaga">‚úÖ Vaga</option>
              </select>
            </div>

            <div className="preview-texto">
              <h4>Pr√©-visualiza√ß√£o do Texto:</h4>
              <div className="texto-preview">
                {tiposDeclaracao[tipoDeclaracao].template(aluno).split('\n').map((linha, i) => (
                  <p key={i}>{linha}</p>
                ))}
              </div>
            </div>

            <div className="modal-actions">
              <button 
                className="btn-preview"
                onClick={() => gerarPDF(false)}
              >
                üëÅÔ∏è Ver Preview PDF
              </button>
              <button 
                className="btn-download"
                onClick={() => gerarPDF(true)}
              >
                ‚¨áÔ∏è Baixar Direto
              </button>
              <button className="btn-cancel" onClick={onClose}>
                ‚ùå Cancelar
              </button>
            </div>
          </>
        ) : (
          <>
            <h2>üëÅÔ∏è Preview da Declara√ß√£o</h2>
            
            <div className="pdf-preview">
              <iframe 
                src={pdfUrl} 
                width="100%" 
                height="500"
                title="Preview da Declara√ß√£o"
              />
            </div>

            <div className="preview-actions">
              <button 
                className="btn-download"
                onClick={() => gerarPDF(true)}
              >
                ‚¨áÔ∏è Baixar PDF
              </button>
              <button 
                className="btn-print"
                onClick={() => window.print()}
              >
                üñ®Ô∏è Imprimir
              </button>
              <button 
                className="btn-whatsapp"
                onClick={compartilharWhatsApp}
              >
                üì± WhatsApp
              </button>
              <button 
                className="btn-email"
                onClick={compartilharEmail}
              >
                üìß Email
              </button>
              <button 
                className="btn-back"
                onClick={() => setMostrarPreview(false)}
              >
                ‚Ü©Ô∏è Voltar
              </button>
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default DeclaracaoModal;
